return {
    -- NOMES E DESCRIÇÕES
    --heist_trai = "Lost in Transit",
    --heist_trai_hl = "Lost in Transit",
    heist_trai_crimenet = "O Exército está movendo algumas placas de impressão novas para uma casa da moeda no Oeste. Vão usar um trem perto de Fort Worth para fazer isso. Nós encontramos o trem deles e pegamos essas placas, nós poderemos dar nosso preço no mercado negro. Mas tem um probleminha. O Exército bloqueou cada rua por quilômetros ao redor, sem chance de que você escape pela estrada. Eu vou deixar que você arranje uma solução.\n\n» Localize o Trem de Carga do Exército\n» Roube as Placas de Impressão\n» Escape da Ferrovia",
    heist_trai_briefing = "Se alguma coisa vale a pena ser roubada, nós vamos encontrar uma forma de roubá-la. Nós não somos do tipo que foge de um desafio. Mas esse é um desafio do tipo bem sangrento. Essa ferrovia está lotada de vagões de carga, então nós vamos precisar encontrar um manifesto de envio. Mas depois de embalarmos o saque, nós não podemos simplesmente ir embora. Talvez a melhor maneira de se roubar um trem seja roubando o trem. Está me entendendo?\nLembre-se: as coisas vão ser bem mais suaves se o Exército nunca souber que estamos aqui.",

    -- LEGENDAS
    gem_trai_01_01 = "Meus olhos na ferrovia dizem que o portão é eletrificado. Então a não ser que você queira ficar torradinho, não toque-o até desativar a energia.",
    gem_trai_01_02 = "Fiquem atentos, eles tem eletricidade correndo por aquele portão. Não vão tentar atravessá-lo antes de desativarem a energia.",
    gem_trai_01_03 = "Cuidado. Eu ouvi dizer que esse é um portão elétrico. Primeiro desligue a energia, ou encontre uma maneira de contornar isso.",
    gem_trai_02_01 = "Quer um conselho? O metal no teto desse trem é mais fraco. Um maçarico deve derretê-lo feito manteiga.",
    gem_trai_02_02 = "Uma dica: use seu maçarico no teto do vagão. O metal é mais fraco lá.",
    gem_trai_02_03 = "Meu contato diz que a proteção do trem fica mais fina no teto. Se eu fosse você, eu tentaria atravessar derretendo-o por lá.",
    gem_trai_03_01 = "Você não pode sair pela estrada por conta do ponto de controle do Exército. Então você vai pegar as placas deste trem... e escapar em um trem diferente? Mal posso esperar pra ver isso.",
    gem_trai_03_02 = "Vocês vão realmente tentar sequestrar um trem para passar pelos bloqueios do Exército? Acho que só vou acreditar vendo uma porra dessas.",
    gem_trai_03_03 = 'Se vocês fugirem, vão estar levando o termo "roubo de trem" a um novo patamar.',
    gem_trai_04_01 = "Uau. Isso foi... UAU.",
    gem_trai_04_02 = "Mas o que diabos foi isso?!",
    gem_trai_04_03 = "Essa foi a pior imitação de sotaque Texano que já ouvi!",
    gem_trai_05_01 = "Aquele portão é eletrificado. Corte a energia ou encontre outra forma de entrar. Apressem-se pessoal! Aquelas placas estão saindo da estação!",
    gem_trai_05_02 = "Não toque naquele portão sem ter desligado a carga elétrica. Vocês não vão conseguir roubar aquelas placas se estiverem fritos feito uma batatinha!",
    gem_trai_05_03 = "Andem logo, pessoal! E lembrem-se, aquele portão vai torrá-los bastante se não desligarem a energia.",
    gem_trai_06_01 = "Boom! Aquelas placas não vão a lugar nenhum sem nós. Gostei do plano de vocês.",
    gem_trai_06_02 = "Isso aí! Esses trens estão aqui para ficar. Boa jogada, pessoal.",
    gem_trai_06_03 = "Bom trabalho! Aquelas placas não estarão saindo enquanto não estivermos bem preparados.",
    gem_trai_07_01 = "PSI: o metal nestes trens é mais fraco no teto. Um maçarico deve atravessá-lo derretendo direto.",
    gem_trai_07_02 = "Ouvi dizer que esses vagões são mais fracos no teto. Tente cortar por lá.",
    gem_trai_07_03 = "Meu contato diz que vocês terão mais facilidade se derreter esses vagões pelo teto.",
    gem_trai_08_01 = "Vocês não podem escapar pela estrada por conta dos pontos de controle do Exército, e vocês bloquearam a maioria dos trilhos com aquele encanamento. Como vocês vão escapar?",
    gem_trai_08_02 = "O Exército trancou as estradas. A maioria dos trilhos estão bloqueados. Eu não sei que truque vocês têm na manga, mas é melhor você darem um jeito nisso.",
    gem_trai_08_03 = "Com o Exército nas estradas e o encanamento nos trilhos, eu não sei como vocês vão se safar dessa. Espero realmente que vocês tenham um plano.",
    gem_trai_09_01 = "Puta merda! Isso pode realmente segurar a maldita coisa! Isso não é uma doidera...",
    gem_trai_09_02 = "Bem, estou realmente surpresa. Falando sério, eu não pensei que isso ia funcionar.",
    gem_trai_09_03 = "Vocês só podem estar brincando! Esse é um guindaste forte pra cacete!",
    gem_trai_10_01 = "Acelerem a marcha, pessoal! Ouvi dizer que o Exército está se preparando para explodir os trilhos da ferrovia. Uma equipe de demolição está a caminho!",
    gem_trai_10_02 = "O Exército está mandando uma equipe para explodir os trilhos! Saiam daí, se não vão ficar presos!",
    gem_trai_10_03 = "Parece que o Exército descobriu seu plano de fuga! Eles estão se preparando para explodir os trilhos. Saiam daí!",
    gem_trai_11_01 = "Essa é uma foto estranha para se ter na parede sem motivo. Talvez tenha algo naquele lugar que vale a pena ser checado?",
    gem_trai_12_01 = "Toda chave possui uma fechadura. Está vendo alguma pista de onde pode estar?",
    gem_trai_13_01 = "Então, é para isso que a estrela servia! Se eu fosse você eu guardaria esse USB. Pode ser um pedaço de um quebra-cabeça.",
    loc_trai_01_01 = "Hora de um bom e velho roubo de trem! Deve ter algumas maneiras diferentes de passar pelo portão da frente. Façam suas escolhas, meus amigos.",
    loc_trai_01_02 = "Use qualquer método que você puder para passar por aquele portão da frente. Existe mais de uma maneira de se esfolar um gato... Putz, isso é um ditado pesado, não é?",
    loc_trai_01_03 = "Essa é a ferrovia a frente. A segurança no portão da frente não parece muito reforçada. Façam seu trabalho.",
    loc_trai_02_01 = "Passe pelo portão da frente da forma que você quiser.",
    loc_trai_02_02 = "Você pode atravessar cortando através desse portão se conseguir desligar a energia primeiro.",
    loc_trai_02_03 = "Me pergunto se tem alguma forma de passar por aquele portão...",
    loc_trai_03_01 = "O próximo portão parece reforçado. Hmm... sabe, se você encontrar o walkie-talkie de um trabalhador jogado por aqui, talvez eu consiga enganá-lo para nos deixar entrar.",
    loc_trai_03_02 = "Sem chance de passar em silêncio do segundo portão. Nós vamos ter que enganar esses idiotas a abrirem o portão para nós. Veja se um dos trabalhadores deixou seu walkie-talkie para fora. Vou ver o que posso fazer.",
    loc_trai_03_03 = "Vocês acham que conseguem pôr as mãos no walkie-talkie de um dos trabalhadores? Talvez possamos passar pelo segundo portão sem levantar um dedo.",
    loc_trai_04_01 = "Nós temos alguns garotos da manutenção atrasados para o turno deles. Abra o portão.",
    loc_trai_04_02 = "Alguns sargentos estão aqui para uma inspeção de segurança. Deixem-os entrar.",
    loc_trai_04_03 = "Meu turno já acabou, mas eu deixei as chaves do meu carro no meu armário. Você pode me deixar entrar?",
    loc_trai_05_01 = "Encontre um walkie, e eu acharei alguém para abrir o portão.",
    loc_trai_05_02 = "Alguma sorte procurando o walkie-talkie?",
    loc_trai_05_03 = "Um dos trabalhadores provavelmente deixou seu walkie-talkie jogado aqui fora.",
    loc_trai_06_01 = "Tudo certo. Sigam para o armazém e encontrem uma maneira de entrar no escritório do administrador. Tem um manifesto de entrega lá dentro que vai nos guiar até as placas.",
    loc_trai_06_02 = "Deve haver um manifesto de entrega que vai nos dizer qual trem tem as placas. Chequem o escritório de administrador no armazém.",
    loc_trai_06_03 = "Se vocês conseguirem chegar até o escritório do administrador dentro do armazém, você será capaz de acessar o manifesto de entrega com informações sobre as placas.",
    loc_trai_07_01 = "Vocês vão precisar de um código para o escritório. Chequem em volta do armazém. Tem sempre algum tipo de pista.",
    loc_trai_07_02 = "A porta do escritório precisa de um código. Está escrito em algum lugar?",
    loc_trai_07_03 = "O escritório do administrador está trancado por um teclado? Que merda. Vejam se conseguem descobrir esse código.",
    loc_trai_08_01 = "A informação que precisamos está dentro do escritório de administrador.",
    loc_trai_08_02 = "Vocês realmente precisam entrar nesse escritório.",
    loc_trai_08_03 = "Já acharam o código para o escritório?",
    loc_trai_09_01 = "Hackeiem o computador do administrador para puxar esse manifesto. Nós precisamos ver qual empresa de envio o Exército está usando para varrer as merdas por baixo do tapete.",
    loc_trai_09_02 = "O Exército está escondendo os vagões de trem deles sob um nome de empresa falso. Hackeiem o computador do administrador, e então o manifesto mostrará a direção correta.",
    loc_trai_09_03 = "Podem puxar o manifesto com o nome da empresa falsa de entrega do Exército que está no computador do administrador.",
    loc_trai_10_01 = "Vamos acessar esse manifesto de entraga, não é?",
    loc_trai_10_02 = "Deem uma olhada no manifesto de entrega.",
    loc_trai_10_03 = "Hackeiem o computador do administrador.",
    loc_trai_11_01 = "Agora nós sabemos qual trem roubar, mas o nosso contato diz que os vagões estão equipados com sensores de movimento. Esses merdinhas devem estar conectados a um console de controle em algum lugar da ferrovia.",
    loc_trai_11_02 = "Antes de entrarmos dentro dos vagões, precisamos cuidar dos sensores de movimento equipados no trem. O contato da Gemma diz que eles são conectados a um console de controle em algum lugar na ferrovia.",
    loc_trai_11_03 = "Nós temos informação de que os vagões do Exército estão cobertos de sensores de movimento. Procure em volta por um console de controle, então use-o para desligar esses merdinhas.",
    loc_trai_12_01 = "Encontre o console que controla os sensores de movimento. Nós não podemos fazer nada até eles estarem desativados.",
    loc_trai_12_02 = "Nós precisamos desligar os sensores de movimento. Procure ao redor por aquele console de controle.",
    loc_trai_12_03 = "Nós não podemos chegar até as placas de impressão até você desligar aqueles sensores de movimento.",
    loc_trai_13_01 = "Eu já vi essa tecnologia antes. Esses idiotas militares têm os seus consoles conectados a um painel de geração de chaves. Se você conseguir encontrá-lo, ele vai nos dizer o código de acesso.",
    loc_trai_13_02 = "Eu reconheço essa configuração de segurança. O código de acesso do console está sendo produzido por um painel de geração de chaves em algum lugar próximo. Vamos encontrá-lo.",
    loc_trai_13_03 = "Nossa, já faz tempo que não vejo isso. O console está conectado a algo chamado painel de geração de chaves. Você vai precisar encontrá-lo se quiser desligar os sensores.",
    loc_trai_14_01 = "Tem que haver um painel de geração de chaves em algum lugar por aqui. ",
    loc_trai_14_02 = "O painel de geração de chaves deve nos dar o código para os sensores de movimento.",
    loc_trai_14_03 = "Eu sei que tem um maldito painel de geração de chaves em algum lugar por aqui.",
    loc_trai_15_01 = "Vê essa grade de luz? Ela deve corresponder diretamente ao código chave. Volte ao console e digite isso lá.",
    loc_trai_15_02 = "O arranjo dessas luzes deve corresponder o código do console de controle. Nós podemos desligar os sensores de movimento agora!",
    loc_trai_15_03 = "O código chave do console deve ser como as luzes nesse painel. Volte e digite-o lá.",
    loc_trai_16_01 = "Agora deve ser seguro se aproximar dos vagões. Procurem pelos que tem o nome da transportadora do manifesto.",
    loc_trai_16_02 = "Os sensores de movimento foram desligados. Agora nós podemos entrar dentro dos vagões. Procurem pelos da transportadora falsa.",
    loc_trai_16_03 = "Sensores fora do ar! Agora, vamos pegar essas placas de impressão. Os vagões estaraão marcados com o nome daquela transportadora que você viu no manifesto de entrega.",
    loc_trai_17_01 = "Vamos pegar essas placas, amigos. Encontrem os vagões marcados com o nome da transportadora falsa.",
    loc_trai_17_02 = "Vá até os vagões com a logo da transportadora falsa. As placas vão estar dentro deles.",
    loc_trai_17_03 = "Nós sabemos quais vagões possuem as placas de impressão. Só precisamos encontrá-los",
    loc_trai_18_01 = "É isso. As placas de impressão devem estar nestes vagões.",
    loc_trai_18_02 = "Você achou! Esse é o vagão com as placas de impressão.",
    loc_trai_18_03 = "Esse é o vagão do Exército. As placas tem que estar dentro desse vagão de trem.",
    loc_trai_19_01 = "Hmm, o metal desse trem parece muitíssimo denso. Eu me lembro do nosso contato dizer algo sobre maçaricos serem guardados no armazém. Isso poderá nos ajudar a resolver o problema.",
    loc_trai_19_02 = "Oof, as trancas nesses vagões não estão para brincadeira. Seria muito mais fácil só cortar uma entrada pelo exterior. O contato da Gemma mencionou algo sobre maçaricos dentro do armazém. Vão lá olhar.",
    loc_trai_19_03 = "A armadura nesse trem parece resistente, mas eu aposto que conseguiriamos passar facilmente com um pouco de calor extremo. Gemma disse que eles têm alguns maçaricos no armazém. Isso é exatamente o que precisamos.",
    loc_trai_20A_01 = "Um maçarico! Sempre uma ferramente boa para se ter em mão.",
    loc_trai_20A_02 = "Você achou um maçarico! Isso pode ser útil.",
    loc_trai_20A_03 = "Eu aposto que podemos encontrar um uso para esse maçarico.",
    loc_trai_20_01 = "Vão pegar um maçarico no armazém. Vamos precisar de um para entrar no vagões.",
    loc_trai_20_02 = "Eles têm alguns maçaricos dentro do armazém. Vão pegá-los.",
    loc_trai_20_03 = "Encontrem esses maçaricos. Eles provavelmente os mantém trancados em algum lugar.",
    loc_trai_21_01 = "Hora de assegurarem essas placas de impressão. Vocês devem ter tudo que vão precisar para entrarem dentro desses vagões.",
    loc_trai_21_02 = "Vocês estão prontos para invadirem esses vagões. Placas de impressão, aqui vamos nós!",
    loc_trai_21_03 = "Vocês já sabem o que fazer. Entrem nos vagões e peguem essas placas.",
    loc_trai_22_01 = "Vamos embalar essas placas de impressão.",
    loc_trai_22_02 = "Entrem nesses vagões de trem.",
    loc_trai_22_03 = "Vamos pegar essas placas hoje ainda, certo?",
    loc_trai_23_01 = "Parece que nós temos uma caixa de IDRF no cofre. Chequem dentro dela pelo fio que temos que achar, e quando ele estiver cortado, vocês poderão desativar a tranca.",
    loc_trai_23_02 = "Nossa velha amiga, a caixa de IDRF! Beleza, chequem dentro dela pelo fio colorido que temos que achar, então cortem o fio correspondente do lado de fora do vagão.",
    loc_trai_23_03 = "Nós já lidamos com caixas de IDRF antes. Se vocês checarem dentro, vai ver qual fio colorido precisamos cortar do lado de fora do vagão. Vocês sabem, blablabla.",
    loc_trai_24A_01 = "Vocês tem certeza de que esse foi o fio certo?",
    loc_trai_24A_02 = "Eu não acho que esse seja o certo...",
    loc_trai_24A_03 = "Fio errado! Talvez nós tivemos sorte...Talvez não.",
    loc_trai_24B_01 = "Boa memória! O cofre deve estar aberto agora.",
    loc_trai_24B_02 = "É isso! A caixa de IDRF não será mais um problema.",
    loc_trai_24B_03 = "Vocês escolheram o certo! Que alívio.",
    loc_trai_24_01 = "Os fios da IDRF devem estar conectados do lado de fora do vagão. Tenha certeza de cortar o certo.",
    loc_trai_24_02 = "Os fios coloridos da IDRF são conectados ao exterior do vagão. Não corte o errado por acidente.",
    loc_trai_24_03 = "Lembrem-se amigos: se vocês cortarem o fio errado, vão disparar o alarme, e isso irá dificultar pra caralho a nossa vida aqui.",
    loc_trai_25_01 = "Excelente! Vocês já podem levar as placas para o nosso trem de fuga.",--Excellent! You can load the plates onto our getaway train.",
    loc_trai_25_02 = "Ótimo! Movam as placas pro trem que marcamos para a fuga.",--Nicely done! Move those plates to the train we marked for our escape.",
    loc_trai_25_03 = "Levem essas placas pro nosso trem de fuga. Não deixem nenhuma pra trás!",--Get those plates over to our escape train. Don't leave any behind!",
    loc_trai_26_01 = "Agora temos tudo que precisamos no trem, nós precisamos abastecer e deixar pronto pra partir. Preciso entrar no sistema dos alto-falantes da ferrovia. Vou fazer parecer que está tudo normal aqui.",--Now that we've packed up the getaway train, we need it fueled and ready to go. Hook me into the railyard's PA system. I'll make our work seem like business as usual.",
    loc_trai_26_02 = "Nós precisamos preparar o nosso trem para a fuga, mas não podemos chamar muita atenção na ferrovia. Se vocês me conectarem no sistema dos alto-falantes da ferrovia, eu posso fazer parecer que está tudo de acordo com a rotina.",--We need to prep our train for the escape, but we can't draw any attention around the railyard. If you connect me to the PA system, I can make our work look routine.",
    loc_trai_26_03 = "Antes da gente abastecer o trem de fuga, me conectem no sistema dos auto-falantes da ferrovia. Irei anunciar para fazer o nosso trabalho aqui não parecer suspeito.",--Before we fuel up the getaway train, hook me to the railyard's PA system. I'll make an announcement to keep our work from looking suspicious.",
    loc_trai_27_01 = "Atenção! Olá! Hã, nós temos uma mudança de planos, bem rapidinho. Trem C-33 precisa ser abastecido e preparado para partir. Então, vamos! Vagabundos!",--Attention! Howdy! Uh, we've got ourselves a schedule change, right quick. Train C-33 needs to be fueled and ready for departure. So let's move, y'all! Dagnabbit!",
    loc_trai_27_02 = "Opa, galera! Nós temos uma ordem do chefia pra preparar o trem C-33 para partir. Então se você vê o trem se movendo, não fiquem de chilique. Está tudo sob controle. Zé buceta.",--Howdy, y'all! We got an order from the top dogs to prep Train C-33 for an unscheduled departure. So if you see that train on the move, don't throw a hissy fit. It's all good. Peachy keen.",
    loc_trai_27_03 = "Há uma mudança nos horários de partida! Trem C-33 precisa partir mais rápido do que um frando no... hã... qual é o feriado mesmo? Vai sair em breve. Só isso mesmo!",--There's been a lil' change to the departure schedule! Train C-33 needs to head out of here faster than a turkey on...Uh...What's that blerrie holiday...it's leaving soon. That's all!",
    loc_trai_28_01 = "Me conectem no sistema de alto-falantes. Andei praticando o meu Texano na semana passada.",--Connect me to the PA system. I've been working on my Texan all week.",
    loc_trai_28_02 = "Me conectem no sistema de alto-falantes. Eu tenho um sotaque de Texano prontinho.",--Hook me up to the PA system. I've got a Texas accent all ready to go.",
    loc_trai_28_03 = "Espera até ouvirem o meu sotaque de Texano! Vocês nem vão me reconhecer.",--Wait until you hear my Texas accent! You won't even recognize me.",
    loc_trai_29_01 = "Tem um funcionário chegando aí! A gente vai ser pego no ato se enrolar. Amarrem ele, depois terminamos de mover a locomotiva.",--There's a worker in our way! We'll get caught if we wait around too long. Go tie him down, then finish prepping the locomotive.",
    loc_trai_29_02 = "Parece que um dos funcionários não caiu na minha mensagem. Eu não entendi isso! Temos que prender ele pra continuarmos a preparar a locomotiva.",--Looks like one of the workers didn't buy my accent. I don't understand that! We have to tie him up so we can prep the locomotive.",
    loc_trai_29_03 = "Nós não podemos preparar a locomotiva com aquele funcionário chegando! Cuidem dele. Evitem ser letal, se puderem.",--We can't prep the locomotive with that worker in the way! Go take care of him. Non-lethal, if you can manage.",
    loc_trai_30_01 = "Nós precisamos desacoplar a locomotiva, abastecer e tirá-la daí.",--We need to detach the locomotive, fuel it up, and get it moving.",
    loc_trai_30_02 = "Já terminaram de preparar o trem?",--Done prepping the train yet?",
    loc_trai_30_03 = "Tempo é dinheiro, amigos. Preparem o trem logo.",--Time is money, friends. Get that train ready to move.",
    loc_trai_31_01 = "Quase lá! Só precisamos achar os controles daquela plataforma giratória. Esses sistemas geralmente precisam de um cartão de acesso, então procurem por um funcionário que tenha um.",--Almost done! Just need to find the controls to that turntable. These systems usually require an access card, so look out for a worker that's got one handy.",
    loc_trai_31_02 = "Última coisa que precisamos é rotacionar aquela plataforma giratória. Fiquem de olho em algum funcionário aí. Se o sistema precisar de um cartão de acesso, teremos um em mãos.",--Last thing we need to do is rotate that turntable. Find the controls, and keep an eye out for any workers in the vicinity. If the system needs an access card, they'll have one.",
    loc_trai_31_03 = "Último passo: precisamos colocar o nosso trem na posição certa. Procurem por controles daquela plataforma giratória, e se aquilo precisar de cartão de acesso, um dos funcionários devem ter um.",--Final step: we need to get our train facing the right way. Look for the controls to the turntable, and if it requires an access card, one of the workers should have what you need.",
    loc_trai_32_01 = "Encontrem os controles daquela plataforma giratória. Talvez precise de um cartão de acesso pra poder usar.",--Find the controls to that turntable. You might need to lift a keycard to gain access.",
    loc_trai_32_02 = "Sistemas como as plataformas giratórias requerem um cartão de acesso. Vocês provavelmente acharão um com um dos funcionários.",--Systems like the turntable usually need some kind of access card. You'll likely find it on one of the workers.",
    loc_trai_32_03 = "Aquele trem não vai a lugar nenhum até vocês rotacionarem a plataforma.",--That train's not going anywhere until you rotate the turntable.",
    loc_trai_33_01 = "Quase pronto! Só precisa reposicionar a plataforma. Usem o cartão de acesso na sala de controle.",--Almost done! Just need to reposition the turntable. Take that keycard you found to the control room.",
    loc_trai_33_02 = "Última coisa que precisamos é rotacionar a plataforma. Usem o cartão de acesso para acessarem os controles.",--Last thing we need to do is rotate that turntable. Use that keycard you found to access the controls.",
    loc_trai_33_03 = "Último passo: precisamos colocar o nosso trem na direção certa. Esse cartão de acesso deve permitir os controles da plataforma.",--Final step: we need to get our train facing the right way. That access card you found should unlock the controls for the turntable.",
    loc_trai_34_01 = "Esse cartão de acesso permite os controles da plataforma. Muito bom!",--That keycard unlocks the controls to the turntable. Very good find!",
    loc_trai_34_02 = "Esse cartão pode ser útil. Vai permitir o acesso dos controles da plataforma.",--Hang on to that card. It'll give you access to the turntable controls.",
    loc_trai_34_03 = "Eu acho que esse cartão de acesso é para os controles da plataforma giratória. Pode ser útil depois.",--I think that's the access card for the turntable controls. Should come in handy.",
    loc_trai_35_01 = "Boa! Agora usem os controles para rotacionar o trem para a direção certa.",--Good! Now use the controls to point our train in the right direction.",
    loc_trai_35_02 = "Rotacionem a plataforma giratória.",--Go ahead and rotate the turntable.",
    loc_trai_35_03 = "Agora controlem a plataforma. Molezinha igual apertar um botão.",--Now, take control of the turntable. Easy as pressing a button.",
    loc_trai_36_01 = "Usem o cartão de acesso para ajustarem a plataforma giratória.",--Use the access card to adjust the turntable.",
    loc_trai_36_02 = "Destravem os controles da plataforma.",--Unlock the controls to the turntable.",
    loc_trai_36_03 = "Essa plataforma não vai girar sozinha.",--That turntable's not going to turn itself.",
    loc_trai_37_01 = "Merda! Parace que temos um curto circuito na plataforma. Vocês terão que ir lá e consertarem.",--Kak! Looks like there's a short circuit in the turntable mechanism. You'll have to get out there and fix it.",
    loc_trai_37_02 = "Tem algo de errado com a plataforma! Parece que há um curto circuito. Consertem-o assim que possível.",--Something's gone wrong with the turntable! Looks like a circuit shorted out. Fix it as fast as you can.",
    loc_trai_37_03 = "Ah, nem tudo são flores! Há um curto circuito na plataforma. Vocês podem consertá-lo?",--Agh, nothing's easy! There's a short circuit in the turntable. Think you can fix it?",
    loc_trai_38_01 = "Consertem o curto circuito na plataforma. Tomem cuidado quando abrirem!",--Fix the short circuit on the turntable. Be careful out in the open!",
    loc_trai_38_02 = "Vocês precisam consertar a plataforma. Rápido!",--You need to repair the turntable. Quickly!",
    loc_trai_38_03 = "Não deixem ninguém ver vocês enquanto consertem a plataforma.",--Don't let anyone see you while you're fixing the turntable.",
    loc_trai_39_01 = "Conseguiram! Tá, voltem pro trem e bora dar o fora daqui!",--You did it! Okay, get back to the train, and let's ride off into the sunset!",
    loc_trai_39_02 = "Tudo pronto! Todos a bordo no trem de fuga!",--Everything's set! All aboard the getaway train!",
    loc_trai_39_03 = "Vamos dar o fora daqui! Todos a bordo! Sabe, piui piui e sei lá!",--Let's get out of here! Jump on the train! You know, choo-choo and whatnot!",
    loc_trai_40_01 = "A fuga está pronta! Voltem pro trem, amigos!",--The escape's all ready! Back to the locomotive, friends!",
    loc_trai_40_02 = "Hora de cair fora daqui!",--Time to get out of here!",
    loc_trai_40_03 = "Não há necessidade de ficarem enrolando! Voltem pro trem!",--No need to stick around! Get back to the escape train!",
    loc_trai_41_01 = "Caralho! Isso deve ser interessante!",--Blerrie! This should be interesting!",
    loc_trai_41_02 = "Fiquem atentos, idiotas!",--Look alive, chops!",
    loc_trai_41_03 = "Beleza! Preparem as armas!",--Alrighty! Lock and load!",
    loc_trai_42_01 = "Eles estão preparando o trem para escaparem! Entrem no pátio, rápido!",--They're prepping the trains for an escape! Get inside the railyard, quick!",
    loc_trai_42_02 = "Eles estão tentando fugir! Não deixem os trens escaparem!",--They're making a run for it! Don't let those trains escape! ",
    loc_trai_42_03 = "Não enrolem na entrada! Se aqueles trens fugirem, o nosso trabalho vai pra casa do caralho!",--Hurry into the railyard! If those trains get away, the job's finished!",
    loc_trai_43_01 = "O relógio está correndo, seus imbecis! Temos que abrir o primeiro portão!",--Clock's ticking, you chops! Get through the first gate!",
    loc_trai_43_02 = "Bora, abram o portão! Não há tempo a perder!",--Come on, get past the gate! There's no time to lose!",
    loc_trai_43_03 = "Se apressem, ou aquelas placas vão embora!",--Pick up the pace, or we can kiss those plates goodbye!",
    loc_trai_44_01 = "O segundo portão está bem reforçado, mas podemos entrar pela garagem de carregamento. O contato da Gemma escondeu thermite em algum lugar aí. Ache-o!",--The second gate's heavily reinforced, but we can get around it through the loading garage. Gemma's contact hid some thermite nearby. Find it!",
    loc_trai_44_02 = "O contato da Gemma escondeu thermite por aí. Aquilo não vai servir no próximo portão, mas permitirá a gente entrar na garagem de carregamento que leva para o pátio de trens.",--Gemma's insider hid some thermite nearby. It won't do much on the next gate, but it will get us into a loading garage that connects to the railyard.",
    loc_trai_44_03 = "O próximo portão não está para brincadeira. Nós vamos ter alguma sorte com a garagem de carregamento. Procurem por uma bolsa de thermite que o contato da Gemma deixou pra gente.",--The next gate isn't messing around. We'll have better luck passing through the loading garage. Look for a bag of thermite Gemma's insider left us.",
    loc_trai_45_01 = "Bora! Usem a thermite na garagem!",--Come on! Use thermite to get through the garage!",
    loc_trai_45_02 = "Quando acharem a thermite, usem-a para entrar na garagem!",--When you find the thermite, use it to get inside the loading garage!",
    loc_trai_45_03 = "Usem a thermite na garagem! Estamos perdendo tempo!",--Use thermite on the garage door! We're wasting time! ",
    loc_trai_46_01 = "Vamos ter que impedir os trens de fugirem! Parece que o Exército estaciounou alguns humvees ao redor do pátio. Vejam se eles estão carregando explosivos.",--We've got to stop the trains from leaving! It looks like the Army parked some humvees around the railyard. See if they're carrying explosives.",
    loc_trai_46_02 = "Nós não podemos deixar os trens fugirem com as nossas placas! Vejam se vocês conseguem achar alguns explosivos naqueles humvees do Exército.",--We can't let the trains take off with our plates! See if you can find some explosives in those Army humvees.",
    loc_trai_46_03 = "Nós vamos precisar de alguns explosivos para manter aqueles trens presos. Chequem aqueles humvees estacionados no pátio.",--We're going to need some explosives to keep those trains from leaving. Check the humvees parked around the yard.",
    loc_trai_47_01 = "Chequem os humvees por explosivos.",--Check the humvees for explosives.",
    loc_trai_47_02 = "Viram se tem algum explosivo naqueles humvees?",--See any explosives in those humvees?",
    loc_trai_47_03 = "Aposto que vão achar explosivos naqueles veículos do Exército.",--I bet you'll find explosives in those army vehicles.",
    loc_trai_48_01 = "Boa! Plantem esses explosivos nos canos em cima dos trilhos. Vou explodi-los quando estiverem prontos, bloqueará o caminho deles.",--Good find! Rig those explosives up to that pipeline hanging over the tracks. I'll blow it down when you're clear, and it'll block the escape.",
    loc_trai_48_02 = "Plantem essas C4s nos canos acima dos trilhos. Vou detonar quando estiverem numa área segura, a explosão deve parar os trens.",--Set those explosives to the pipeline above the tracks. I'll detonate when you're a safe distance, and the blast should stop the trains from leaving.",
    loc_trai_48_03 = "Estou pensando, se nós explodirmos os canos acima dos trilhos, nós podemos prender os trens. Plantem os explosivos. Eles não vão explodir até vocês estiverem seguros.",--I'm thinking, if we blow down the pipeline above the tracks, we can block the trains in. Rig the explosives. They won't trigger until you're clear.",
    loc_trai_49A_01 = "Três... Dois... Um!",--Three...Two...One!",
    loc_trai_49A_02 = "Preparado... E... Cabum!",--Ready...Set...Boom!",
    loc_trai_49A_03 = "Lá... Vamos... Nós!",--Here...We...Go!",
    loc_trai_49_01 = "Agora, saiam da área da explosão!",--Now, clear the blast area!",
    loc_trai_49_02 = "Caiam fora da área da explosão!",--Get out of the blast zone!",
    loc_trai_49_03 = "Protejam-se, amigos!",--Take cover, friends!",
    loc_trai_50_01 = "Plantem esses explosivos nos canos! O trem vai ir embora a qualquer segundo!",--Plant those explosives on the pipeline! The train could leave any second!",
    loc_trai_50_02 = "Os trens estão indo embora! Plantem os explosivos, rápido!",--The trains are leaving! Set the explosives, quick!",
    loc_trai_50_03 = "Armem nos canos para explodirem! Antes que seja tarde!",--Rig the pipeline to blow! Before it's too late!",
    loc_trai_51_01 = "Agora, vamos entrar no escritório do administrador para ver qual trem está carregando as placas. A porta provavelmente está trancada, então já preparem a furadeira!",--Now, we've got to get inside the admin office to find out which trains are carrying the plates. Door's probably under lockdown, so get drilling!",
    loc_trai_51_02 = "Hora de descobrir qual trem está carregando as placas. Usem a furadeira na porta do escritório do administrador. É lá onde fica o manifesto de cargas.",--Time to find out which train's carrying the plates. Drill your way into the admin office. That's where they store the shipping manifest.",
    loc_trai_51_03 = "O manifesto de cargas está no escritório do administrador que nos mostrará qual trem está com as placas. Usem a furadeira na porta.",--There's a shipping manifest in the admin office that'll tell us which train has the plates. Use your drill to get inside.",
    loc_trai_52_01 = "Usem a furadeira para entrar no escritório do administrador!",--Drill your way into the admin office!",
    loc_trai_52_02 = "Arrombem a porta do escritório do administrador!",--Break into the admin office!",
    loc_trai_52_03 = "Entrem no escritório do administrador!",--Get inside the admin office!",
    loc_trai_53_01 = "Hackeiem o computador e puxem o manifesto. Isso nos mostrará qual empresa é o disfarce.",--Hack the computer and pull up the manifest. It'll tell us which shipping company is a front.",
    loc_trai_53_02 = "Puxem o manifesto naquele computador. Nós precisamos saber qual é a empresa que está disfarçando o Exército.",--Bring up the shipping manifest on that computer. We need to know which brand is a front for the Army.",
    loc_trai_53_03 = "O manifesto de cargas está no computador do administrador. Hackeiem para gente saber onde está as placas.",--The shipping manifest is stored on the admin computer. Hack in so we can see where they're keeping the plates.",
    loc_trai_54_01 = "Usem o computador! A segurança de informações deve ser uma porra.",--Get on the computer! The cyber security here should be kak.",
    loc_trai_54_02 = "Vocês devem conseguir hackear aquele computador, fácil!",--You should be able to hack that computer, easy!",
    loc_trai_54_03 = "Precisamos dar uma olhada no manifesto!",--We need to get a look at that manifest!",
    loc_trai_55_01 = "Tá bom, nós sabemos qual trem está carregando as placas. Localizem os vagões com a estampa da empresa falsa.",--Alright, we know which train is carrying the printing plates. Find the wagons marked with the fake shipping brand.",
    loc_trai_55_02 = "Então essa é a empresa falsa. Localizem os vagões com essa estampa. Eles terão as placas.",--So that's the fake shipping company. Locate the wagons with that brand. They'll have the printing plates inside.",
    loc_trai_55_03 = "Essa empresa é um disfarce pro Exército. Achem os vagões, e acharão as placas.",--That shipping brand is a cover for the Army. You find those wagons, you'll find the printing plates.",
    loc_trai_56_01 = "Achem os vagões com a empresa do manifesto.",--Find the wagons marked with the shipping company from the manifest.",
    loc_trai_56_02 = "Procure pelos vagões da empresa falsa.",--Look for the wagons with the fake shipping brand.",
    loc_trai_56_03 = "Agora sabemos quais vagões têm as placas. Só precisa achá-las.",--Now we know which wagons have the plates. Just need to find them.",
    loc_trai_57_01 = "Parece que temos um metal grosso nesses vagões. O nosso contato disse alguma coisa sobre os maçaricos no depósito. Vão lá pegar!",--Looks like thick metal on those train wagons. Our contact said something about blowtorches stored in the warehouse. Go grab them!",
    loc_trai_57_02 = "Alguns maçaricos cortariam direto no metal do vagão. O contato da Gemma disse que guardam no depósito.",--Some blowtorches would burn right through the armor on those wagons. Gemma's insider says they keep some locked up in the warehouse.",
    loc_trai_57_03 = "Esses vagões parecem resistentes, mas não ao calor. O nosso contato disse que eles guardam os maçaricos no depósito. Vocês já sabem o que fazer.",--Those wagons look tough, but not necessarily heat resistant. Our insider says they keep blowtorches in the warehouse. You know what to do.",
    loc_trai_58_01 = "Vocês acharão os maçaricos no depósito.",--You'll find blowtorches locked up in the warehouse.",
    loc_trai_58_02 = "Eles guardam os maçaricos no depósito.",--They store blowtorches in the warehouse.",
    loc_trai_58_03 = "Vão buscar esses maçaricos no depósito.",--Go get those blowtorches from the warehouse.",
    loc_trai_59_01 = "As placas estão quase no nosso alcance! Só precisam cortar o teto dos vagões.",--The plates are in reach! Just need to burn your way into the train cars.",
    loc_trai_59_02 = "Vocês estão quase entrando nos vagões. Placas de impressão, aqui vamos nós!",--You're all set to break into those train wagons. Printing plates, here we come!",
    loc_trai_59_03 = "Assim que cortarem os vagões, essas placas vão ser nossas!",--Once you burn your way into the wagons, those plates are as good as ours!",
    loc_trai_60_01 = "Arrombem os vagões e peguem essas placas!",--Break into the wagons and get those plates!",
    loc_trai_60_02 = "Usem o maçarico para entrarem no vagão.",--Use your blowtorch to get inside the train wagon.",
    loc_trai_60_03 = "Bora, idiotas! Não enrolem com o maçarico!",--Come on, chops! Get blowtorching!",
    loc_trai_61_01 = "Tem uma caixa de IDRF na merda do cofre! Já comecem hackeando, e quando terminar, peguem as placas.",--There's an RFID box on the blerrie vault! Start a bypass hack, and when it's unlocked, snag the plates.",
    loc_trai_61_02 = "Eles colocaram essas caixas idiotas de IDRF! O alarme já foi pro saco mesmo. Só comecem a hackear o cofre pra abrir.",--They got those stupid RFID boxes! We're past caring about the alarm. Just do a bypass hack on the vault.",
    loc_trai_61_03 = "Ah, legal, uma caixa de IDRF. A gente ama isso. Hackeiem isso para abrir o cofre.",--Oh good, an RFID box. We love those. Do a bypass hack to open the vault.",
    loc_trai_62_01 = "Mantenham os policiais longe da fiação fora do vagão!",--Make sure the kerels don't mess with any wiring outside the wagon!",
    loc_trai_62_02 = "O hack vai falhar se qualquer um atrapalhar na fiação fora do vagão.",--The hack will fail if anyone tampers with the wires outside the wagon.",
    loc_trai_62_03 = "Tem uma rede de fios do IDRF no lado de fora do vagão. Protejam eles.",--There's a network of RFID wires on the wagon's exterior. Keep them safe.",
    loc_trai_63_01 = "Policial filha da puta cortou o fio do IDRF! Vocês terão que reiniciar o hack.",--Damn kerel cut an RFID wire! You'll have to reboot the hack.",
    loc_trai_63_02 = "Alguma coisa aconteceu com um dos fios do IDRF! Reiniciem o hack!",--Something happened to one of the RFID wires! Go restart the bypass!",
    loc_trai_63_03 = "O hack parou! Podem reiniciá-lo?",--The hack stalled! Can you reboot it?",
    loc_trai_64_01 = "Seguem o plano! Levem as placas para o nosso trem de fuga.",--Stick to the plan! Load the plates onto our getaway train.",
    loc_trai_64_02 = "Peguem essas placas e levem para o nosso trem de fuga! Vocês estão indo bem!",--Get those plates to our escape train! You're doing great! ",
    loc_trai_64_03 = "Mantenham o foco, agora! Levem essas placas para o nosso trem de fuga.",--Stay focused, now! Take the plates to our getaway train.",
    loc_trai_65_01 = "Essa fuga pode ser um pouco doida, amigos. Mas antes de tudo: bora abastecer a nossa locomotiva e desacoplar dos vagões.",--This escape might be a little wild, friends. First thing's first: get our locomotive fueled up and detach it from the wagon.",
    loc_trai_65_02 = "Antes de fazer qualquer coisa, vamos precisar abastecer a nossa locomotiva e desacoplar dos vagões.",--Before we do anything else, you need to fuel up our locomotive and disconnect it from the wagons.",
    loc_trai_65_03 = "Nós vamos sair daqui dando um passo de cada vez. Passo um: Abastecer a locomotiva e desacoplar os vagões.",--We'll take this getaway one step at a time. Step one: get that locomotive fueled and unhook it from the train cars.",
    loc_trai_66_01 = "Bora preparar a locomotiva! Quanto mais rápido, melhor!",--Get the locomotive ready to go! Faster you work, the faster we can leave!",
    loc_trai_66_02 = "Não enrolem, amigos! Preparem a locomotiva para fugirem!",--Let's not waste any time, friends! Prep the locomotive for our escape!",
    loc_trai_66_03 = "Nós precisamos preparar o trem para a partida! Se apressem!",--We need to get that train ready for departure! On the double!",
    loc_trai_67_01 = "Tá, aí vem a parte complicada. A plataforma giratória está trancada, mas há um guindaste aí para levantar carga pesada. Espero que seja forte bastante para levantar a nossa locomotiva, dedos cruzados. Usem os controles para botar na posição.",--Okay, here's the tricky part. The turntable's locked, but there's a crane on-site for lifting heavy cargo. Fingers crossed it's strong enough to move our locomotive to an open track. Work the controls to get it into position.",
    loc_trai_67_02 = "Prestem atenção agora. Eles desligaram a plataforma giratória, mas há um guindaste ainda funcionando. Eu tenho... setenta por certo de certeza que pode levantar a locomotiva e levá-la para os trilhos. Usem os controles e bora descobrir!",--Bear with me on this. They shut down the turntable, but the on-site cargo crane is still up and running. I'm...seventy percent sure it can lift our locomotive to an open track. Get to the controls, and let's find out!",
    loc_trai_67_03 = 'Com a plataforma giratória desligada, o guindaste vai ser melhor para mover a locomotiva para os trilhos. Vão para os controles, bora torcer que seja uma ideia "tão idiota que funciona".',--With the turntable locked down, the railyard s cargo crane is our best shot for moving the locomotive to an open track. Head for the controls, and let s hope this is a "so crazy it just might work" situation.',
    loc_trai_68_01 = "Controlem o guindaste pra locomotiva. É nossa melhor chance para dar o fora daqui!",--Maneuver the crane over the locomotive. It's our best chance for getting out of here!",
    loc_trai_68_02 = "Posicionem o guindaste!",--Position the crane!",
    loc_trai_68_03 = "Movam o guindaste para a posição!",--Get the crane into position!",
    loc_trai_69_01 = "Vocês vão precisar conectar os ganchos do guindaste. Essa máquina não foi construída pra essas loucuras.",--You'll need to secure the crane hooks manually. That machine wasn't built for this craziness.",
    loc_trai_69_02 = "Tá, agora subam no trem e conectem os ganchos. Quanto mais apertado, melhor.",--Okay, now get on top of the train and secure the hooks. Tighter the better.",
    loc_trai_69_03 = "Aquele guindaste foi feito pra carregar cargas pesadas, não trens de fuga. Vocês terão que conectar os ganchos.",--That crane was made to lift cargo crates, not escape trains. You'll have to secure the hooks manually.",
    loc_trai_70_01 = "Conectem os ganchos no trem!",--Fasten the hooks to the top of our train!",
    loc_trai_70_02 = "Bora conectar os ganchos!",--Let's get those hooks secure!",
    loc_trai_70_03 = "Subam no topo do trem e conectem os ganchos!",--Get on top of the train and secure those hooks!",
    loc_trai_71_01 = "Momento de honestidade. Usem o guindaste para mover a locomotiva. Finjam que é uma brincadeira de gancho, só que é grande, e com muita emoção!",--Moment of truth. Use the crane to move our locomotive. Pretend it's one of those claw games! Just bigger! And with much higher stakes!",
    loc_trai_71_02 = "Ok, tá. Operem o guindaste para levantar a locomotiva no trilho certo. Ah, porra, não consigo ver isso...",--Okay. Alright. Operate the crane to lift our locomotive to the right track. Ah blerrie, I can't watch...          ",
    loc_trai_71_03 = "Tá muito bom! Agora usem os controles do guindaste para mover a locomotiva. Tenho certeza que funcionará! Hã, talvez não tanto assim, mas estou bem otimista!",--Looks good! Now use the crane controls to move our locomotive. I'm positive this will work! Er, maybe not positive, but I'm very optimistic!",
    loc_trai_72_01 = "Ative o guindaste, espero que isso funciona!",--Activate the crane. I sure hope this works!",
    loc_trai_72_02 = "Bora ver o que esse guindaste é capaz de fazer!",--Let's see what this crane can do!",
    loc_trai_72_03 = "É agora ou nunca! Liguem o guindaste!",--It's now or never! Turn on the crane!",
    loc_trai_73A_01 = "Mantenham os policiais longe dos controles do guindaste!",--Keep the kerels away from the crane controls!",
    loc_trai_73A_02 = "Os policiais vão bagunçar com os controles do guindaste se vocês deixarem!",--The kerels will mess with the crane controls if you let them!",
    loc_trai_73A_03 = "Não deixem os policiais bagunçarem com os controles do guindaste!",--Don't let the kerels mess with the crane controls!",
    loc_trai_73B_01 = "Eles pararam o guindaste! Vocês precisam arrumar os controles!",--They stalled the crane! You have to fix the controls!",
    loc_trai_73B_02 = "Um policial ferrou os controles! Consertem, rápido!",--A kerel got to the controls! Fix it, quick!",
    loc_trai_73B_03 = "Os policiais pararam o guindaste! Andem logo!",--The kerels stopped the crane! Get it moving again!",
    loc_trai_73_01 = "Merda, o guindaste parou! Reparem a caixa do circuito, rápido! Esses ganchos não vão segurar isso pra sempre!",--Ack, the crane shorted out! Repair the circuit box, fast! Those hooks won't hold forever!",
    loc_trai_73_02 = "O guindaste teve um curto circuito! Consertem antes que os ganchos quebrem!",--The crane's got a short circuit! Fix it before the hooks give out!",
    loc_trai_73_03 = "Merda! O guindaste teve algum problema elétrico. Deem um jeito, rápido! Nós não queremos abusar da sorte!",--Kak! The crane's got some electrical problem. Sort it out, quick! We don't want to press our luck!",
    loc_trai_74_01 = "Beleza! Bora dar o fora daqui. Liguem o motor!",--Amazing! Let's get the hell out of here. Ignite the engine!",
    loc_trai_74_02 = "Fantástico! Liguem o motor, bora dar o fora!",--Fantastic work! Fire up the engine, and let's head out!",
    loc_trai_74_03 = "Trabalho bem feito! Agora, liguem o motor para a gente escapar daqui!",--Damn fine job! Now, start the engine so we can make our escape!",
    loc_trai_75_01 = "Vamos, idiotas! Liguem a porra do motor!",--Come on, chops! Start your blerrie engines!",
    loc_trai_75_02 = "Conseguimos o que a gente queria. Liguem o motor!",--We got what we came for. Activate the engine!",
    loc_trai_75_03 = "Liguem o motor! Não há razão para ficarem enrolando aí!",--Turn on the engine! No point hanging around!",
    loc_trai_76_01 = "Hora de darmos o fora. Todos a bordo, amigos!",--Time to make our escape. All aboard, friends! ",
    loc_trai_76_02 = "Bora dar o fora daqui! Entrem no trem!",--Let's head out! Jump on the train!",
    loc_trai_76_03 = "O trem de fuga está aceitando passageiros! Entrem!",--The escape train is now boarding! Hop on!",
    loc_trai_77_01 = "Hora de ir! Entrem no trem!",--Time to go! Hop on the train!",
    loc_trai_77_02 = "Entrem no trem!",--Get on the train!",
    loc_trai_77_03 = "Nós terminamos aqui!",--We're done here!",
    loc_trai_78_01 = "Precisamos do trem na direção certa! Hackeiem os controles daquela plataforma giratória!",--We need our train pointing in the right direction! Go hack the controls to that turntable!",
    loc_trai_78_02 = "Estamos perto! Hackeiem a plataforma giratória para colocar o trem na direção certa!",--We're so close! Hack the turntable controls to get our train facing the right way!",
    loc_trai_78_03 = "Se vocês hackearem os controles da plataforma giratória, podemos rotacionar a nossa locomotiva para a liberdade!",--If you hack the controls to that turntable, we can aim our locomotive towards freedom!",
    loc_trai_79_01 = "Nós não podemos escapar com o nosso trem na direção errada!",--We can't escape if our train's facing the wrong way!",
    loc_trai_79_02 = "Controlem a plataforma giratória!",--Take control of the turntable!",
    loc_trai_79_03 = "Hackeiem os controles da plataforma giratória!",--Hack the turntable controls!",
    loc_trai_80_01 = "Merda! Teve um curto circuito na plataforma giratória! Vão lá e deem um jeito nisso!",--Kak! There's a short circuit in the turntable! Get out there and fix it!",
    loc_trai_80_02 = "Tem algo de errado com o mecanismo da plataforma! Consertem assim que puderem!",--Something's wrong with the turntable mechanism! Fix it as fast as you can!",
    loc_trai_80_03 = "A plataforma teve um curto circuito! Acham que podem consertar essa porra?",--The turntable short circuited! Think you can fix it?",
    loc_trai_81_01 = "Vocês têm que consertar a plataforma!",--You've got to fix the turntable!",
    loc_trai_81_02 = "Nós não podemos sair até que a plataforma giratória esteja consertada!",--We can't leave until the turntable's fixed!",
    loc_trai_81_03 = "Vão lá e consertem a plataforma!",--Get out there and fix that turntable!",
    loc_trai_end_loud_01 = "Saiu um pouco do planejado, mas depois de sermos pagos pela as placas, valerá a pena!",--It got a little hairy there for a bit, but with the cash we'll get for these plates, it's all worth it!",
    loc_trai_end_stealth_01 = "Um roubo de trem que ficará para a história! O Texas foi fantástico para nós.",

    -- OBJETIVOS [as strings "_hl" têm um limite de 48 caracteres, se passar disso, o texto ficará bugado, a menos que o usuário usa algum mod que aumenta esse limite, mas é bem improvável.]
    hud_heist_trai01_hl = "Entre no Posto de Segurança",
    hud_heist_trai01 = "Procure por uma maneira de passar o primeiro portão para acessar o posto de segurança da ferrovia.",
    hud_heist_trai02_hl = "Encontre um Walkie-Talkie",
    hud_heist_trai02 = "Tem um walkie-talkie em alguma parte desta área. Use-o para chamar um guarda e convencê-lo de abrir o segundo portão.",
    hud_heist_trai03_hl = "Encontre o Código para o Escritório do Administrador",
    hud_heist_trai03 = "A porta para o escritório da administração está trancada com um keypad. Procure pelo armazém para ver se o código não está escrito em algum lugar.",
    hud_heist_trai04_hl = "Puxe o Manifesto de Envio",
    hud_heist_trai04 = "Hackeie o computador do administrador do Exército para encontrar quais vagões de carga estão carregando as placas de impressão.",
    hud_heist_trai05_hl = "Encontre um Maçarico",
    hud_heist_trai05 = "Você vai precisar de um maçarico para abrir os vagões do trem. Deve ter um escondido em algum lugar do armazém.",
    hud_heist_trai06_hl = "Encontre o Console de Sensor de Movimento",--Find the motion sensor console",
    hud_heist_trai06 = "Os vagões do trem estão conectados com sensores de movimentos. Antes que vocês possam roubar as placas, precisarão achar o console de alarme e desativar os sensores.",--The train wagons are rigged with motion sensors. Before you can reach the printing plates, you need to find the alarm console and deactivate the sensors.
    hud_heist_trai07_hl = "Encontre o Painel de Gerador de Chave",--Find the keygen panel
    hud_heist_trai07 = "O código para o console de sensor de movimento está conectado a um dispositivo chamado painel de gerador de chave. Achem-o e hackeiem o painel para revelar o código.",--The code for the motion sensor console is linked to a device called a keygen panel. Find it, then hack the panel to uncover the code.
    hud_heist_trai08_hl = "Invada os Vagões e Assegure as Placas",--Break into the wagons and secure the plates
    hud_heist_trai08 = "Cortem o telhado dos vagões, roubem as placas dos cofre, e assegurem na nossa locomotiva de fuga.",--Cut through the roof of the wagons, retrieve the printing plates from their vaults, and move them to the escape locomotive.
    hud_heist_trai09_hl = "Conecte o Locke no Sistema de Alto-falante",--Connect Locke to the PA system
    hud_heist_trai09 = "Vocês precisam preparar a locomotiva para a fuga, mas também não tem que parecer suspeito. Conectem o Locke no sistema dos alto-falantes, assim, ele poderá anunciar a partida do trem.",--You need to prepare your locomotive for escape, but the work can't look suspicious. Patch Locke into the PA system so he can announce the train's departure.
    hud_heist_trai10_hl = "Prepare a Locomotiva para a Partida",--Prepare the locomotive for departure
    hud_heist_trai10 = "Antes que possam mover a locomotiva nos trilhos, precisam abastecer a locomotiva e desaclopar dos vagões.",--Before you move your locomotive down the tracks, it needs to be fueled up and detached from its wagons.
    hud_heist_trai11_hl = "Roube um Cartão para a Plataforma Giratória",--Steal a keycard for the turntable
    hud_heist_trai11 = "Vocês precisam rotacionar a locomotiva para a direção certa. Encontrem um cartão de acesso para acessarem os controles da plataforma giratória.",--You need to point the locomotive in the right direction. Find a worker's keycard to access the turntable controls.
    hud_heist_trai12_hl = "Rotacione a Locomotiva",--Rotate the locomotive
    hud_heist_trai12 = "A locomotiva está na posição. Usem os controles da plataforma giratória para rotacionar os trilhos.",--The locomotive is almost in position. Use the turntable controls to rotate the tracks.
    hud_heist_trai13_hl = "Conserte o Mecanismo da Plataforma Giratória",--Fix the turntable mechanism
    hud_heist_trai13 = "A Fiação da plataforma teve curto circuito. Consertem para continuarem com a rotação do trem.",--There's a short circuit in the wiring under the turntable. Fix it to complete the train's rotation.
    hud_heist_trai14_hl = "Fuga Disponível",--Escape available
    hud_heist_trai14 = "Vocês conseguiram o que queriam. Entrem no trem para escaparem.",--You have what you came for. Hop in the locomotive to escape.
    hud_heist_trai15_hl = "Corte a Porta da Garagem",--Cut through the garage door
    hud_heist_trai15 = "O portão principal é forte demais para arrombarmos. Usem a thermite para cortarem a porta da garagem.",--The main gate is too strong to breach. Use thermite to cut through the loading garage door instead.
    hud_heist_trai16_hl = "Roube as Cargas Explosivas",--Steal explosive charges
    hud_heist_trai16 = "Vejam se os humvees do Exército têm algum explosivo.",--See if the Army Humvees are packed with any explosives.
    hud_heist_trai17_hl = "Plante os Explosivos nos Canos",--Plant explosives on the pipeline
    hud_heist_trai17 = "Plantem os explosivos nos dois lados dos canos, depois explodam para impedirem os trens de escaparem.",--Set up explosives on both ends of the pipeline, then blow it up to block the trains from escaping.
    hud_heist_trai18_hl = "Use a Furadeira na Porta do Escritório",--Drill through the office door
    hud_heist_trai18 = "O escritório do administrador está bem trancado. O único jeito de entrar seria usando a furadeira na porta.",--The admin office is under lockdown. The only way to get inside is by drilling through the door.
    hud_heist_trai19_hl = "Puxe o Manifesto de Entregas",--Pull up the shipping manifest
    hud_heist_trai19 = "Hackeiem o computador do administrador para acharem qual empresa está carregando as placas de impressão.",--Hack the admin computer to find out which freight train is carrying the printing plates.
    hud_heist_trai20_hl = "Encontre um Maçarico",--Find a blowtorch
    hud_heist_trai20 = "Vocês precisarão de um maçarico para cortarem os vagões dos trens. Deve estar guardado em algum lugar no depósito.",--You will need a blowtorch to cut open the train wagons. It should be stashed somewhere in the warehouse.
    hud_heist_trai21_hl = "Recupere a Serra",--Retrieve the placeable saw
    hud_heist_trai21 = "O contato da Gemma escondeu uma serra para ajudarem vocês a abrir os cofres. Encontrem-o.",--Gemma's insider hid a placeable saw to help you crack open the vaults. Find it.
    hud_heist_trai22_hl = "Preparem a Locomotiva para a Partida",--Prepare the locomotive for departure
    hud_heist_trai22 = "Antes de moverem a locomotiva para os trilhos, precisaremos abastecer e desacoplar os vagões.",--Before you move your locomotive down the tracks, it needs to be fueled up and detached from its wagons.
    hud_heist_trai23_hl = "Ative o Guindaste",--Activate the crane
    hud_heist_trai23 = "Vocês precisarão mover a locomotiva para os trilhos abertos, mas a plataforma giratória foi desligada. Posicionem o guindaste de cargas para levantar o trem para onde precisar ir.",--You need to move the locomotive to an open track, but the turntable has been locked down. Position the shipping crane so that it can lift the train where it needs to go.
    hud_heist_trai24_hl = "Conecte os Ganchos",--Fasten the hooks
    hud_heist_trai24 = "Vocês precisam conectar os quatro ganchos do guindaste para levantar a locomotiva.",--You must attach the 4 crane hooks to the top of the locomotive.
    hud_heist_trai25_hl = "Levante a Locomotiva",--Lift the locomotive
    hud_heist_trai25 = "Usem os controles do guindaste para mover a locomotiva para os trilhos abertos.",--Use the crane controls to move the locomotive to an open track.
    hud_heist_trai26_hl = "Ligue o Motor da Locomotiva",--Start the locomotive's engine
    hud_heist_trai26 = "Abram o painel no lado do trem e liguem o motor.",--Open the panel on the side of the train and start the engine.
    hud_heist_trai27_hl = "Fuga Disponível",--Escape available
    hud_heist_trai27 = "Vocês conseguiram o que queriam. Entrem no trem para escaparem.",--You have what you came for. Hop in the locomotive to escape.
    hud_heist_trai28_hl = "Localize os Vagões Certos",--Find the right wagons
    hud_heist_trai28 = "Vocês sabem qual é o disface para o Exército. Achem os vagões certos.",--You know which shipping brand is a front for the Army. Find the corresponding wagons.
    hud_heist_trai29_hl = "Repare os Cabos do Guindaste",--Repair the crane cables
    hud_heist_trai29 = "Há um curto circuito do sistema elétrico do guindaste. Consertem-o.",--There's a short-circuit in the crane's electrical system. Fix it quickly.
    hud_heist_trai30_hl = "Hackeie os Controles da Plataforma Giratória",--Hack the Turntable Controls
    hud_heist_trai30 = "Os controles da plataforma giratória foram desligados! Hackeiem o sistema para rotacionar o nosso trem de fuga.",--The controls to the turntable have been locked down! Hack the system to rotate our escape train.
    hud_heist_trai31_hl = "Vá para o Depósito",--Head for the warehouse
    hud_heist_trai31 = "O escritório de administrador é a nossa próxima parada. É apenas acessível do depósito, vão pra lá.",--The admin office is our next stop. It's only accessible from the warehouse, so head that way.
    hud_heist_trai32_hl = "Reinicie o Guindaste",
    hud_heist_trai32 = "A policia parou o guindaste! Reiniciem os controles antes que o Exército exploda os trilhos de trem.",
    hud_heist_trai33_hl = "Evacue a Área da Explosão",
    hud_heist_trai33 = "Saiam do raio da explosão, a menos que você queira ter seus membros espalhados por toda Fort Worth.",
}