	hud_heist_big_mission9_hl = "Monte a Besta",--Assemble the Beast
	hud_heist_big_mission10_hl = "Perfure o Cofre",--Drill into the vault
	hud_heist_big_mission3_hl = "Encontre e Hackeie o Computador Correto",--Find and hack the correct computer
	hud_heist_big_mission13_hl = "Fuga Disponível!",--Escape available!
	hud_heist_big_mission10 = "Instale a furadeira para entrar no cofre.",--Drill your way into the vault.
	hud_heist_big_mission24 = "O helicoptero de fuga vai chegar no telhado do banco, coloque as bolsas lá.",--The escape helicopter will arrive soon on the roof of the bank, get the bags over there.
	hud_heist_big_mission15_hl = "Conceda acesso a partir dos Consoles de Segurança",--Grant access from the security consoles
	hud_heist_big_mission3 = "Descubra qual é o computador e acesse-o localmente. Na pior das hipóteses, você poderá sempre hackear todos eles.",--Figure out which staff computer it is and access it locally. Worst case scenario you can always hack them all.
	hud_heist_big_mission21 = "Pegue a bolsa que contém a termite.",--Pick up the bag containing the thermite.
	hud_heist_big_mission28 = "Entre no Benevolent Bank.",--Enter the Benevolent Bank.
	hud_heist_big_mission12_hl = "Assegure os Espólios",--Secure the loot
	hud_heist_big_mission23_hl = "Espere a termite terminar de queimar",--Wait for the thermite to finish burning
	hud_heist_big_mission13 = "A fuga já está disponível!",--The escape is now available!
	hud_heist_big_mission6 = "Devemos reconectar o sistema de segurança para iniciar o bloqueio de tempo.",--We must re-wire the security system to start the time lock.
	hud_heist_big_mission9 = "Use as partes da furadeira para montar a Besta.",--Use the drill parts to set up A Besta.
	hud_heist_big_mission16 = "Etapa de identificação 3/3: Acesse o computador do gerente do banco para obter os códigos do cofre.",--Identification step 3/3: Access the bank managers computer to get hold of the vault codes.
	hud_heist_big_mission4 = "Use o keypad para iniciar o bloqueio de tempo na porta que abrirá o portão de segurança para a área do cofre.",--Use the keypad to start the time lock on the door which will open the security gate to the vault area.
	hud_heist_big_mission11 = "Pegue todo espólio que você encontrar no cofre.",--Bag all the loot you can find in the vault.
	hud_heist_big_mission4_hl = "Inicie o Bloqueio de Tempo",--Start the time lock to open the security gate
	hud_heist_big_mission22 = "Coloque a termite em cima do cofre.",--Place the thermite over the vault.
	hud_heist_big_mission6_hl = "Religue o Bloqueio de Tempo",--Re-wire the time lock
	hud_heist_big_mission18 = "Instale a tirolesa no telhado e suba para o guindaste.",--Set up the zip-line on the roof and get up to the crane.
	hud_heist_big_mission27 = "Use o telefone para sinalizar o ônibus para atravessar a parede.",--Use the phone to signal to make the bus drive through the wall.
	hud_heist_big_mission25 = "Force a abertura das portas do elevador para que possamos acessar a ventilação do elevador.",--Force open the elevator doors so we can access the elevator shaft.
	hud_heist_big_mission19_hl = "Rotacione o Guindaste para que fique em cima da Clarabóia",--Rotate the crane so it is above the skylights
	hud_heist_big_mission23 = "Aguarde até que a termite queime até a parte interna do cofre.",--Hold the fort until the thermite has burned through the vault.
	hud_heist_big_mission20 = "Solte o porco cofre do guindaste.",--Release the piggybank from the crane.
	hud_heist_big_mission8_hl = "Pegue as Bolsas que contêm as partes da Furadeira",--Pick up the bags containing the drill parts
	hud_heist_big_mission28_hl = "Entre no Banco",--Enter the bank
	hud_heist_big_mission2_hl = "Obtenha Acesso Remoto ao Computador",--Get remote access to the computer
	hud_heist_big_mission2 = "Use o servidor para obter acesso remoto até o computador que contêm os códigos que podem abrir o portão de segurança para a área do cofre.",--Use the server to get remote access to the computer containing the codes to unlock the security gate to the vault area.
	hud_heist_big_mission26 = "Coloque os explosivos C4 na parede e exploda para fugir.",--Place the C4 explosive charges on the wall and blow it up in order to escape.
	hud_heist_big_mission21_hl = "Pegue a termite",--Pick up the thermite
	hud_heist_big_mission20_hl = "Solte o Porco Cofre",--Drop the piggy bank
	hud_heist_big_mission5 = "Espere pelo bloqueio de tempo abrir o portão de segurança.",--Wait for the timelock to open the security gate.
	hud_heist_big_mission7 = "Os policiais pararam o bloqueio de tempo. Use o keypad para iniciá-lo novamente.",--The cops stoped the time lock. Use the keypad to start it again.
	hud_heist_big_mission14 = "Etapa de identificação 1/3: Escaneie o polegar no leitor de impressão digital.",--Identification step 1/3: Scan the thumb in the fingerprint scanner.
	hud_heist_big_mission27_hl = "Sinalize o Ônibus",--Signal the bus
	hud_heist_big_mission7_hl = "Reinicie o Bloqueio de Tempo",--Unpause the time lock
	hud_heist_big_mission1 = "Descubra onde a sala do servidor está localizada e acesse-a.",--Find out where the server room is located and access it.
	hud_heist_big_mission14_hl = "Use o Leitor de Impressão Digital",--Use the fingerprint scanner
	hud_heist_big_mission19 = "Rotacione o guindaste sobre o banco para soltar o porco cofre.",--Rotate the crane over the bank to drop the piggy bank.
