	loc_pent_70_03 = "Ativem a caixa de energia para ligar a luz sinalizadora.",--"Activate the power box to turn on the signal light.",
	loc_pent_53_01 = "O disco rígido está localizado na sala dos servidores. Wang é paranoico, então está escondida atrás de uma porta secreta. Encontrem-a.",--"The hard drive is located in the server room. Wang is paranoid, so it's hidden behind a secret door. Find it. ",
	loc_pent_03_01 = "Tem três caixas de segurança. Desliguem elas para eu poder acessar o sistema. O que tem a luz verde é o que vocês tem que procurar primeiro.",--"There are three security boxes. Disable them so I can access the system. Whichever has a green light is the current one you need to go for.",
	loc_pent_56_03 = "O disco rígido está dentro daquela sala de servidores!",--"The hard drive is right inside that server room!",
	loc_pent_68_01 = "Ufa! Bom trabalho! Aquele foi dificil.",--"Whew! Well done! That was a tough one.",
	loc_pent_67_01 = "Droga, ele tem armadura pesada e um lança-chamas. Wang não está de brincadeira!",--"Blerrie, he's got heavy armor and a flamethrower. Wang's not messing about!",
	loc_pent_68_03 = "Ufa, essa passou perto! Bom trabalho!",--"Damn, that was a close one! Well done!",
	loc_pent_91_01 = "A armadura nas suas costas é muito forte; ele não vai tomar dano pelas costas!",--"The armor on his back is extra strong; he won't take any damage from behind!",
	loc_pent_08_02 = "Encontrem a caixa de fusíveis para o elevador e desliguem-a.",--"Find the fuse box for the elevator and disable it.",
	loc_pent_29_02 = "Vamos lá! Cuidem dele. Não vamos ficar mais jovens.",--"Let's go! Take care of this prick. We're not getting any younger.",
	loc_pent_83_02 = "Não deixem a policia mexer com o dispositivo de hack. Tá?",--"Make sure the cops don't mess with the hacking device, yeah?",
	loc_pent_32_02 = "Aquele cartão de acesso deve abrir o outro amontoado de ouro.",--"That keycard must open the other gold stash.",
	loc_pent_19_02 = "Vocês precisam entrar naquela sala! Furem a porta, ou encontrem o código.",--"You need to get into that server room! Drill the door, or find the code. ",
	loc_pent_41_02 = "Certo, o hack está pronto. Desçam para a garagem e chamem o elevador. O poço precisa estar limpo para vocês chegarem no apartamento.",--"All right, the hack is done. Head down to the garage and call the elevator. The shaft needs to be clear so you can get up to the penthouse.",
	loc_pent_11_02 = "Certo, abram aquele elevador e vão para o último andar.",--"All right, pry that elevator open and get to the top floor.",
	loc_pent_91_02 = "Atirem nele na cara ou no corpo!",--"Shoot him in the face or the chest!",
	loc_pent_71_01 = "Não vamos sair sem nada no nossos bolsos. Usem o cartão e peguem o ouro.",--"We're not leaving without lining our pockets. Use the keycard and go for the gold.",
	loc_pent_10_03 = "Vão para os elevadores assim que acharem as ferramentas!",--"Get to the elevator as soon as you find those tools!",
	loc_pent_36_02 = "Há! Quem quer a vida quieta, né?",--"Ha! Who wants the quiet life, eh?",
	loc_pent_65_02 = "Wang está fugindo! Parece que ele vai pro telhado. Vão! O tempo dele acabou.",--"Wang is running away! Looks like he is going to the roof. Move! His time is up.",
	loc_pent_04_01 = "Achem as caixas de segurança. Lembrem, luz verde significa ativado, tá?",--"Go for those security boxes. Remember, green light means active, yeah?",
	loc_pent_06_02 = "Chamem o elevador pela garagem.",--"Call the elevator from the garage.",
	loc_pent_07_02 = "Agora façam o elevador não se mover. Encontrem a caixa de fusíveis e religuem-a.",--"Now make sure the elevator doesn't move. Find the fuse box and rewire it.",
	loc_pent_10_01 = "Peguem as ferramentas, e vão para o elevador. Elas estão em uma van na garagem.",--"Get those tools, and head for the elevator. They're in a van in the garage.",
	loc_pent_11_03 = "Usem o pé de cabra para abrir o elevador. E usem a tirolesa até o topo.",--"Use the crowbar to force open the elevator. Then zipline to the top.",
	loc_pent_21_01 = "Agora, o disco está criptografado. Demoraria meses para abrir. Mas podemos evitar isso. Wang deve ter um protocolo de descriptograficação em seu laptop. Se usarem, vamos quebrar aquele bebê rapidinho.",--"Now, the drive is encrypted. It would take us months to crack. But we can bypass that. Wang should have the encryption protocol on his laptop. If you use that, we'll break open that baby in a flash.",
	loc_pent_22_01 = "Encontrem o laptop de Wang e descriptografem o disco.",--"Find Wang's laptop and decrypt the drive.",
	loc_pent_22_03 = "Precisamos descriptografar o disco. Encontrem o laptop.",--"We need to decrypt the drive. Find the laptop.",
	loc_pent_25_02 = "Wang está rodiado de lacaios na sala de reuniões. Puxem o alarme de incêndio para tira-lo dali. Isso vai fazer as coisas funcionarem e causar um pouco de confusão no processo.",--"Wang is surrounded by lackeys in the meeting room. Pull the fire alarm to get him out of there. That'll get things rolling and cause a bit of confusion in the process.",
	loc_pent_27_02 = "Aquele desgraçado fugiu para o telhado! Subam lá e acabem com ele!",--"The bastard fled to the roof! Get up there and kill him!",
	loc_pent_29_03 = "Vão em frente! Matem o desgraçado!",--"Go on! Kill that bastard!",
	loc_pent_30_03 = "É assim que se faz! Wang não tinha chance! Agora peguem o cartão.",--"That's how it's done! Wang didn't stand a chance! Now, get his keycard.",
	loc_pent_34_01 = "Bom trabalho. Se estiverem prontos para sair, vão pela plataforma de limpeza. Temos muito para celebrar.",--"Great job. If you're ready to leave, extract by the window cleaning platform. We have a lot to celebrate.  ",
	loc_pent_36_03 = "Certo, vamos causar uns problemas!",--"All right, let's cause some ruckus!",
	loc_pent_37_03 = "Hackeiem o computador da recepção para me dar acesso a rede do prédio.",--"Hack into the reception computer to give me access to the building's network.",
	loc_pent_54_02 = "A sala dos servidores está atrás de uma porta secreta. Procurem por ela!",--"The server room is behind a secret door. Look for it!",
	loc_pent_60_01 = "Peguem as coisas no porta-malas. Abasteçam o carro, e usem o macaco para levantá-lo",--"Get the gear from the trunk. Fuel the car, and use that jack to hoist it up.",
	loc_pent_65_01 = "Parece que o Wang tá correndo. Ele tá indo pro telhado. Caçe e mate-o. É hora de pôr um fim nisso.",--"Seems like Wang is making a run for it. He's heading for the roof. Hunt him down and take him out. It's time to put an end to this.",
	loc_pent_66_01 = "Wang tá correndo. Ache e lide com ele.",--"Wang is on the run. Track him down and deal with him.",
	loc_pent_76_01 = "Ok, foi uma das caixas.",--"Ok, that's one of the boxes done.",
	loc_pent_81_01 = "Temos uma entrega quente. Pegue e use para queimar a porta.",--"We got a hot delivery. Grab it and use it to burn through the door. ",
	yuw_pent_threat_02 = "Mesmo a persistência de vocês sendo admirável, vocês parecem não entender quem está no comando. Eu posso admitir que seus esforços são admiráveis, mas o erro de vocês foi vir aqui.",--"Though your persistence is admirable, you don't seem to understand who is in charge. I can admit that your efforts are impressive, but your mistake was to come here.",
	yuw_pent_threat_04 = "Ah, agora eu entendo. Alguém têm sussurrado em seus ouvidos. Depois de eu lidar com vocês, eu vou limpar a casa. Ratos nojentos. Não, não, não em minha casa",--"Oh, now I understand. Someone has been whispering in your ear. After I've dealt with you, I'll do some house cleaning. Treachourous rats and vermin. No, no, not in my house.",
	yuw_pent_threat_05 = "Conseguem sentir? Vocês são atraídos a isso, como uma mariposa lutando com uma chama, vocês vão queimar e morrer.",--"Can you feel it? You are drawn to it, and like a moth battling a flame, you will burn and die.",

    -- Objetivos
    hud_heist_pent01_hl = "Infiltre-se e encontre os elevadores",--Infiltrate and find the elevators
    hud_heist_pent01 = "Tem duas maneiras de entrar. Desligar a energia dos detectores de metal, ou entrar pelo escritório.",--There are two ways to get inside. Power off the metal detectors or find a way to go through the office.

    hud_heist_pent02_hl = "Dê acesso de segurança ao operador",--Give the operator security access
    hud_heist_pent02 = "O operador precisa de acesso aos sistemas de segurança do prédio. Desligue as caixas de segurança com as luzes verdes.",--The operator needs access to the building's security systems. Switch off the security boxes with a green light.

    hud_heist_pent03_hl = "Chame o elevador pelo porão",--Call the elevator from the basement
    hud_heist_pent03 = "Vá até o porão e chame um elevador.",--Go to the basement and call down the elevator.

    hud_heist_pent04_hl = "Desligue a energia do elevador",--Disable power to the elevator
    hud_heist_pent04 = "Desligue a energia do elevador para deixar o poço livre para usar a tirolesa até o topo do prédio.",--Disable the elevator's power to leave the shaft free for ziplining to the top of the building.

    hud_heist_pent05_hl = "Acesse o poço do elevador",--Access the elevator shaft
    hud_heist_pent05 = "Pegue as ferramentas da van, abra o elevador do primeiro andar com o pé de cabra e coloque tirolesas nos cabos do elevador.",--Grab the tools from a van, open the first-floor elevator with the crowbar and attach the zipline to the elevator cables.

    hud_heist_pent06_hl = "Vá para o elevador de limpeza",--Get to the window cleaning lift
    hud_heist_pent06 = "O infiltrado tem acesso a plataforma de limpeza. Ligue a luz perto da janela, e ele vai descer para vocês.",--The insider has access to the cleaning platform controls. Turn on the light near the window, and he will lower it for you.

    hud_heist_pent07_hl = "Encontre a sala de servidores escondida",--Find the hidden server room
    hud_heist_pent07 = "O disco rígido está em uma sala de servidores escondida dentro do apartamento. Olhe em volta por algo grande o suficiente para cobrir a porta.",--The hard drive is in a hidden server room inside the penthouse. Look around for something big enough to cover a door.

    hud_heist_pent08_hl = "Encontre o código e roube o disco rígido",--Find the code and steal the hard drive
    hud_heist_pent08 = "O código da porta deve estar em uma anotação em algum lugar no apartamento.",--The code for the gate should be kept on a note somewhere in the penthouse.

    hud_heist_pent10_hl = "Ligue o alarme de incêndio",--Trigger the fire alarm
    hud_heist_pent10 = "A Tríade é exigente sobre sistemas de alarme instalados em seus quarteis generais, então o alarme de incêndio não está conectado ao departamento de bombeiros. Ative para tirar as pessoas da reunião.",--The Triad is picky about outgoing alarm systems installed in their headquarters, so the fire alarm isn't connected to the fire department. Trigger it to lure people from the meeting.

    hud_heist_pent11_hl = "Assassine o líder da Tríade",--Assassinate the Triad leader
    hud_heist_pent11 = "Mate o líder da Tríade em algum lugar do telhado.",--Kill the Triad leader who is somewhere on the roof.

    hud_heist_pent12_hl = "Assegure o ouro",--Secure the gold
    hud_heist_pent12 = "Jogue o ouro no local seguro ou coloque no elevador da cozinha para nosso infiltrado pegá-los.",--Drop off the gold on the secure spot or send it down the kitchen elevator for our insider to pick up.

    hud_heist_pent13_hl = "Fuja usando a plataforma de limpeza",--Extract using window cleaning platform
    hud_heist_pent13 = "Complete a missão usando a plataforma de limpeza.",--Complete the mission by using the window cleaning platform.

    hud_heist_pent14_hl = "Acesse a sala secreta",--Access the secret room
    hud_heist_pent14 = "O líder da Tríade tem ouro guardado no seu apartamento; encontrem e peguem.",--The Triad leader has gold stashed in his penthouse; find and secure it.

    hud_heist_pent15_hl = "Descriptografe e assegure o disco",--Decrypt and secure the drive
    hud_heist_pent15 = "O disco rígido está criptografado e vai demorar uns meses para acessar; Encontre o laptop de Yufu Wang para descriptografá-lo. Quando estiver pronto, coloque no elevador da cozinha.",--The hard drive is encrypted and will take us months to access; find Yufu Wang's laptop to decrypt it. When it's done, send it down using the kitchen elevator.

    hud_heist_pent16_hl = "Hackeie o sistema de segurança",--Hack the building's security
    hud_heist_pent16 = "O prédio está indo a modo de confinamento. Crie uma conexão para o operador desativar os sistemas pelo lado de fora.",--The building is going into lockdown mode. Create a connection for the operator to disable its systems from the outside.

    hud_heist_pent17_hl = "Reinicie o elevador e chame-no",--Restart the elevator and call it
    hud_heist_pent17 = "A entrada do apartamento está bloqueada. Ligue o gerador reserva e chame o elevador.",--The entrance to the penthouse is blocked. Start the backup generator and call for the elevator.

    hud_heist_pent18_hl = "Arrombe a porta do apartamento",--Force open the penthouse door
    hud_heist_pent18 = "Remova a escotilha no topo do elevador, pegue o arame na sala do gerador e conecte o elevador na porta. E atire nos fios do elevador.",--Remove the hatch from the top of the elevator, grab the wire from the generator room and connect the elevator to the door. Then shoot out the elevator cable.

    hud_heist_pent19_hl = "Roube o disco rígido",--Steal the hard drive
    hud_heist_pent19 = "Abra a porta da sala do servidor. Quando dentro, pegue o disco rígido.",--Force open the gate to the server room. When inside, steal the hard drive.

    hud_heist_pent20_hl = "Coloque o carro em posição",--Get the car into position
    hud_heist_pent20 = "Precisamos entrar na sala de reuniões para matar Yufu Wang. Use o carro para arrebentar a porta.",--We need to get into the meeting room to kill Yufu Wang. Use the car to break down the door.

    hud_heist_pent21_hl = "Prepare e abasteça o carro",--Prepare and fuel the car
    hud_heist_pent21 = "Nosso infiltrado colocou gasolina e ferramentas na mala do carro. Prepare o carro para arrebentar a porta da sala de reuniões.",--Our insider contact has placed gasoline and tools in the car's trunk. Prepare the car to break down the meeting room door.

    hud_heist_pent22_hl = "Encontre as chaves e ligue o carro",--Find the keys and start the car
    hud_heist_pent22 = "As chaves do carro estão trancadas em um pequeno cofre. Procure nas salas próximas por ele.",--The car keys are locked in a small safe. Search the nearby rooms for it.

    hud_heist_pent23_hl = "Solte o macaco",--Release the jack
    hud_heist_pent23 = "Quando as rodas estiverem rodando, solte o macaco e deixe o carro passar pela porta em velocidade máxima.",--When the wheels are spinning, release the jack and let the car ram through the door at full speed.

    hud_heist_pent24_hl = "Mate o líder da tríade",--Kill the triad leader
    hud_heist_pent24 = "Yufu Wang fugiu para o telhado. Siga-o e elimine-o.",--Yufu Wang has escaped to the roof. Follow him and eliminate him.

    hud_heist_pent25_hl = "Defenda as luzes de obstrução",--Defend the obstruction lights
    hud_heist_pent25 = "O helicóptero está a caminho. Mantenha as luzes de obstrução ligadas, para o piloto saber que estão prontos para o resgate.",--The helicopter is on its way. Keep the obstruction lights on, so the pilot knows you're ready for pickup.

    hud_heist_pent26_hl = "Sinalize o helicóptero",--Signal the helicopter
    hud_heist_pent26 = "Ligue as luzes de obstrução para deixar o helicóptro saber que estão prontos para o resgate.",--Turn on the obstruction lights to let the helicopter pilot know that you're ready for pickup.

    hud_heist_pent27_hl = "Fuga Disponível",--Escape Available
    hud_heist_pent27 = "Fuja usando o helicóptero.",--Escape using the helicopter.

    hud_heist_pent28_hl = "Coloque o térmite na porta",--Put thermite on the door
    hud_heist_pent28 = "Pegue o térmite no elevador da cozinha e coloque na porta da sala de reuniões.",--Pick up the thermite from the kitchen elevator and place it on the meeting room door.

    hud_heist_pent29_hl = "Espere pelo helicóptero",--Wait for the helicopter
    hud_heist_pent29 = "O helicóptero está a caminho. Espere por ele.",--The helicopter is en route. Wait for it.

    hud_heist_pent30_hl = "Pegue o disco rígido",--Grab the hard drive
    hud_heist_pent30 = "Pegue o disco rígido que está conectado no laptop pois a decriptografação falhou.",--Pick up the hard drive that's connected to the laptop since the decryption failed.
}