	hud_heist_pet1_1 = "Encontre e colete todas as Cabras.",--Find and collect all the goats.
	hud_heist_pet1_1_hl = "Colete as Cabras",--Collect the goats
	hud_heist_pet1_3_hl = "Fuga com o Carro Disponivel",--Escape with car available
	
