	hud_heist_bex3_hl = "Abra a Barreira de Segurança",--Open the security barrier
	hud_heist_bex30 = "Desative os lasers de segurança para evitar que acionem o alarme.",--Shut down the security lasers to avoid triggering the alarm.
	hud_heist_bex30_hl = "Desligue os Lasers",--Shut down the lasers
	hud_heist_bex31 = "Mova qualquer carro fora do caminho para liberar acesso para a van de fuga.",--Move any cars out of the way to clear access for the escape van.
	hud_heist_bex31_hl = "Limpe a Rua",--Clear the street
	hud_heist_bex32 = "Espere até o cunhado do Vlad chegar.",--Wait at the bank until Vlad's brother-in-law arrives.
	hud_heist_bex32_hl = "Espere no Banco",--Wait at the bank
	hud_heist_bex33 = "Conecte a corrente em ambos: o caminhão e a janela do bancário.",--Attach the chain to both the truck and the teller windows.
	hud_heist_bex33_hl = "Conecte a Corrente",--Attach the chain
