Hooks:Add("LocalizationManagerPostInit", "naoehsoqualquerdiamante", function(loc)
	LocalizationManager:add_localized_strings({
	-- Nomes e Descrições
	heist_mus = "O Diamante (The Diamond)",
	heist_mus_hl = "O Diamante (The Diamond)",
	heist_mus_crimenet = "Esse Diamante é histórico. E também é extremamente valioso. O Dentista é uma das poucas pessoas com contatos para roubá-lo. Vocês vão recuperá-lo para um cara anônimo que ele representa.\n\n» Entrem no museu McKendrick\n» Encontre aonde o Diamante está mantido\n» Roubem o Diamante\n» Roubem qualquer outro artefato que vocês encontrarem.",
	heist_mus_briefing = "Esse é um dos grandes. Nós vamos atrás de um grande prêmio - O Diamante. Vocês vão invadir o museu McKendrick, pegar o Diamante, e cair fora.Parece simples. Bom, 'simples' nem sempre significa 'fácil', e essa não vai ser. Então aqui estão os objetivos 'simples'. Invadam o museu. Uma vez que estiverem dentro, chequem o que está entre vocês e o Diamante. Derrubem-o. Uma vez que conseguirem o Diamante, caiam fora.\n\nEssa é para o Dentista. Ele é o único com contatos para roubar o Diamante mais famoso do mundo. Estou com um sentimento que ele está só nos testando. Vendo se nós estamos afim de golpes como esse.",
	
	-- Legendas
	pln_hd1_10_02 = "Certo, a polícia está vindo. Essa fita parece mais com Cães de aluguel do que com Pantera Cor de Rosa. Encontrem a sala de segurança.",
	pln_hd1_08_03 = "Vocês passaram! Agora peguem o Diamante!",
	pln_hd1_28_03 = "Sempre achei que ser um guarda num museu seria um ótimo trabalho. Mas, vocês sabem né, toda aquela coisa de roubo...",
	pln_hd1_18_03 = "Vocês podem assegurar os artefatos por aquela janela. Tenho um cara de confiança pronto para pegá-las, mas só se estiver tudo quieto.",
	pln_hd1_11_01 = "Mas que - eles esconderam o Diamante dentro do chão. OK, então nós teremos que romper a barreira! Metam uma furadeira aí.",
	pln_hd1_06_03 = "O temporizador acabou. Estamos quase lá, pessoal! Abram essa última porta!",
	pln_hd1_26_02 = "Os civis terão os cartões. Consigam um, e coloquem para funcionar nos temporizadorers.",
	pln_hd1_16_03 = "O gás já era! Voltem lá! Retornem ao trabalho.",
	pln_hd1_31_02 = "Esse lugar me dá calafrios.",
	pln_hd1_28_01 = "Olhem esse lugar. Me faz desejar que pudéssemos roubar o prédio inteiro.",
	pln_hd1_18_02 = "Estão vendo aquela janela aberta? Se as cosias não estiverem agitadas, vocês podem assegurar os artefatos por ali. Tenho um cara pronto para pegá-los.",
	pln_hd1_08_01 = "Vocês conseguiram! Ele está ali! Peguem o Diamante!",
	pln_hd1_25_03 = "Mano, eu adorava vir aqui quando criança. Me fazia sentir como um aventureiro, vocês sabem.",
	pln_hd1_14_01 = "Merda, gás, gás!!! Caiam fora! Andem!",
	pln_hd1_15_02 = "OK, vocês já estão fora. O gás vai acabar daqui a pouco - só aguentem.",
	pln_hd1_32_01 = "Esse lugar parece que não é decorado desde os anos 60.",
	pln_hd1_32_03 = "Isso parece o porão do escritório do curador.",
	pln_hd1_10_01 = "Merda, o alarme! Vocês precisam encontrar a sala de segurança. Nós não podemos fazer nada enquanto esse lugar está totalmente fechado!",
	pln_hd1_28_02 = "É um prédio bonito. Sereno. Tentem e mantenham-o desta forma.",
	pln_hd1_17_01 = "Se vocês chamarem o helicóptero, as coisas vão pegar fogo. Não o façam até que estejam prontos.",
	pln_hd1_intro_01_02 = "Aqui está ele. Museu McKendrick. Lar de um Diamante impagável. Vamos lá.",
	pln_hd1_38_01 = "Caixa hackeada. Estou vendo as barreiras deles caindo. Continuem.",
	pln_hd1_24_01 = "Aí está. Mas pisem com cuidado! Esses pisos são sensíveis ao toque. Não pisem neles! Vocês vão precisar criar um caminho seguro até o fim! Usem a caixa de força ao lado.",
	pln_hd1_24_02 = "Ah! Aí está, mas esperem um segundo. Esses pisos são sensíveis ao toque - fiquem fora deles. Vocês podem usar essa caixa de circuito para criar um caminho seguro até o fim.",
	pln_hd1_14_02 = "Gás! Caiam fora daí!",
	pln_hd1_01_03 = "A primeira coisa a se fazer é entrar no museu. A porta da frente é a entrada mais óbvia, mas talvez vocês achem algo menos suspeito.",
	pln_hd1_19_03 = "Tem uma zip-line por aí. É para tirar os artefatos daí, perfeita, quieta e tranquila. Têm muita coisa valiosa aí que vocês podem colocar nela.",
	pln_hd1_39_03 = "Eles não suspeitaram de nada. Isso é ótimo.",
	pln_hd1_33_01 = "Têm muita coisa de valor por aqui. Mas chequem os vidros com alarmes - eles serão os mais genuínos e valiosos. ",
	pln_hd1_02_01 = "OK. Se vamos fazer isso furtivamente, teremos que desabilitar alguns alarmes. Olhem em volta. Procurem no museu. Encontrem as caixas de força que sustentam a segurança. Nós podemos hackeá-las sem soar o alarme.",
	pln_hd1_41_01 = "Andem logo! Coloquem o temporizador para funcionar de novo!",
	pln_hd1_37_01 = "Esse era o piso errado! ",
	pln_hd1_13_02 = "Mantenham os policiais longe da caixa de força. Não deixe que eles a desliguem.",
	pln_hd1_05_01 = "Porra! Os temporizadores estão bloqueados. Os cartões não valem merda nenhuma agora. Hora de abrir esses paineis na força!",
	pln_hd1_07_02 = "Agora, vêem essas luzes? É o caminho de vocês. Sigam no caminho! Pisem no errado e isso aqui vai para casa do caralho.",
	pln_hd1_37_02 = "O que? Esse deve ter sido o pipso errado!",
	pln_hd1_34_01 = "As ruas estão lotadas de policiais! Não vamos escapar por elas. Coloquem um sinalizador e eu vou mandar um helicóptero.",
	pln_hd1_05_02 = "Oh porra! Aí está o temporizador. Esqueçam os cartões. Forcem a abertura pelos painéis.",
	pln_hd1_29_02 = "É louco pensar que isso é da época do César.",
	pln_hd1_09_01 = "Vocês sabem o quanto vale essa pedra?",
	pln_hd1_13_01 = "Certifiquem-se de defender a caixa de força que o hack está ligado. Evitem que os policiais a interrompam!",
	pln_hd1_27_02 = "Vocês acabaram de marcar o nome de vocês na história, pessoal. Os assaltantes vão contar essa história por gerações.",
	pln_hd1_36_01 = "O helicóptero está cheio. Esperem ele voltar.",
	pln_hd1_38_03 = "E... Ótimo. A segurança deles está caindo. Façam outro.",
	pln_hd1_23_03 = "Esse caminho seguro não vai durar para sempre, pessoal. Certifiquem-se de aproveitá-lo. Vocês podem criar ele de novo também.",
	pln_hd1_27_01 = "Vocês conseguiram! O Diamante! E a porra da Payday Gang. Vocês acabaram de escrever o seus nomes na história, senhores.",
	pln_hd1_12_01 = "Vocês estão dentro, mas o alarme está em contagem regressiva. Derrubem o lockdown e aí nós poderemos voltar aos trilhos!",
	pln_hd1_24_03 = "Olhem isso. Esperem! Esses pisos são como teclas. Eles são sensíveis ao toque, então fiquem fora deles. Hackeiem a caixa de circuito ao lado - isso vai criar um caminho seguro para atravessar.",
	pln_hd1_intro_01_03 = "Aí está. O museu McKendrick. Em algum lugar aí dentro tem um Diamante impagável. Vamos pegá-lo.",
	pln_hd1_03_01 = "O sistema de alarmes está desabilitado. Entrem!",
	pln_hd1_38_02 = "Está hackeado, ótimo.",
	pln_hd1_36_03 = "Ótimo, totalmente carregado. Aguentem aí pessoal - O Bile já volta.",
	pln_hd1_35_03 = "O Bile está chegando. Estejam prontos!",
	pln_hd1_27_03 = "A gangue que roubou o Diamante. Impressionante, pessoal!",
	pln_hd1_30_02 = "Os mongóis eram grandes guerreiros. Adoraria vê-los em ação.",
	pln_hd1_22_01 = "Os vidros de amostra estão com o alarme desabilitado agora. Se quiserem levar algumas decorações extra, essa é a hora.",
	pln_hd1_43_01 = "Eles cortaram a força! Coloquem para funcionar de novo!",
	pln_hd1_30_01 = "Genghis Khan. Cravou um império da Coréia até a Bulgaria. Grande guerreiro. Adoraria ele na nossa gangue. ",
	pln_hd1_20_03 = "Merda! Certo, esqueçam de usar a zip-line. O nosso cara teve que vazar quando os policiais chegaram.",
	pln_hd1_12_02 = "Vocês estão dentro, mas o alarme foi ativado antes. Parem-o antes que soe, e nós podemos continuar com isso.",
	pln_hd1_09_03 = "Não acredito que nós realmente conseguimos o Diamante.",
	pln_hd1_40_02 = "Os policiais foderam o temporizador. Reiniciem-o.",
	pln_hd1_34_03 = "Porra! Os policiais cercaram tudo. O único jeito de sair daqui é pelo ar. Acendam um sinalizador e eu vou mandar um helicóptero.",
	pln_hd1_04_03 = "Dois temporizadores... hmmm. OK, nós vamos precisar dos cartões para passar por eles. A segurança daqui é fraca demais para ter um, então foquem nos cientistas. Tenho certeza que alguns deles têm os cartões.",
	pln_hd1_35_02 = "OK, o Bile está chegando em vocês agora! ",
	pln_hd1_25_02 = "Tenho certeza que vi esse lugar em algum filme. Espero que as paredes não cuspam dardos de veneno.",
	pln_hd1_19_01 = "Consegui montar uma zip-line aí. Vocês podem colocar qualquer bolsa nela, e assegurá-las sem fazer barulho. Aproveitem o máximo que puderem.",
	pln_hd1_32_02 = "Dúvido que esses livros sejam de grande valor. Pelo menos para nós.",
	pln_hd1_07_01 = "OK, as luzes da caixa de circuito correspodem aos pisos. Utilize-as como um guia para passar. Ninguém pode pisar em um dos errados, se não isso aqui vira uma putaria sem tamanho!",
	pln_hd1_13_03 = "Protejam essa caixa de força. Os policiais vão tentar desligá-la. Não os deixem!",
	pln_hd1_11_03 = "Porra! Eles esconderam o Diamante dentro do chão! Não temos tempo para dar uma de espertinhos, então, só resta uma coisa a se fazer - metam uma furadeira aí! ",
	pln_hd1_43_02 = "Liguem a força de novo!",
	pln_hd1_intro_01_01 = "Uma noite no museu pessoal. Só tem vocês, uma equipe de segurança e um Diamante impagável.",
	pln_hd1_35_01 = "O helicóptero está a caminho! Fiquem prontos!",
	pln_hd1_01_01 = "Certo. Primeiro objetivo - entrar. A porta da frente está logo aí, mas talvez seja melhor ser um pouco mais esperto e procurar por outros jeitos de entrar. ",
	pln_hd1_01_02 = "O primeiro objetivo é entrar. A porta da frente está logo ali. Mas talvez tenham outros jeitos de entrar.",
	pln_hd1_02_02 = "Certo, vamos tentar isso e derrubar a segurança. Vocês vão precisar encontrar as caixas de força que sustentam o circuito. Se conectem a elas. Eu posso hackeá-las daqui e desativá-las sem que soe o alarme.",
	pln_hd1_02_03 = "Vamos ver se podemos acabar com a segurança. O ponto fraco dela são as caixas de força espalhadas por aí . Se nós conseguirmos hackeá-las, podemos derrubar as barreiras sem soar o alarme.",
	pln_hd1_03_02 = "Os lasers estão desligados! Andem, vamos ver o que nos aguarda.",
	pln_hd1_03_03 = "Essa foi a última caixa. Cortando a força. Pronto! Os lasers já eram! Se mexam!",
	pln_hd1_04_01 = "Próximo passo... Dois temporizadores. Vocês precisam de cartões para passar por eles. Os guardas não os terão, mas os civis devem ter. Encontrem-os e, vocês sabem, rendam-os. Eles vão largar os cartões alguma hora.",
	pln_hd1_04_02 = "Hmmm, dois temporizadores. Certo, vocês vão precisar de cartões para atravessá-los. Essa segurança semi-policial não os terá, mas eu tenho certeza que os cientistas terão. Rendam-os para eles soltarem os cartões.",
	pln_hd1_05_03 = "Merda! Os temporizadores foram reconfigurados. Certo. Esquecem os cartões. Nós vamos forçá-los.",
	pln_hd1_06_01 = "OK, os temporizadores já eram. Agora, desçam e abram essa última porta!",
	pln_hd1_06_02 = "Os temporizadores já eram. Só passem por essa última porta!",
	pln_hd1_07_03 = "As luzes estão mostrando o caminho pela sala. Vocês veem? Cada luz corresponde a um piso. Se mantenham nos pisos certos!",
	pln_hd1_08_02 = "Vocês passaram. Olhem ele. É maravilhoso. Agora peguem-o.",
	pln_hd1_09_02 = "Vocês o pegaram. O Diamante. A porra do Diamante.",
	pln_hd1_10_03 = "Caralho! A polícia está mandando tudo que eles tem. Hora de fazer disso um diamante sanguinário. Encontrem a sala de segurança!",
	pln_hd1_11_02 = "Ei, onde está o Diamante? Eles o esconderam no chão! Não esperava por isso. Estão pedindo por um méodo violento - arrombem isso com uma furadeira!",
	pln_hd1_12_03 = "Estamos dentro, mas o alarme está em contagem regressiva. Encontrem o lockdown e desliguem-o antes que o alarme soe. Vamos tentar manter isso quieto.",
	pln_hd1_14_03 = "Gás! Merda, caiam fora! Agora!",
	pln_hd1_15_01 = "Certo. Esperem um minuto - deixem o gás se dissipar. ",
	pln_hd1_15_03 = "O gás vai acabar uma hora. Esperem um momento. ",
	pln_hd1_16_01 = "OK, o gás já era. Voltem lá!",
	pln_hd1_16_02 = "Gas is cleared. Get back in there. ",
	pln_hd1_17_02 = "O helicóptero está esperando. Chamem-o quando quiserem, mas lembrem-se - qunado ele aparecer, as coisas vão ficar violentas. Escolham o melhor momento.",
	pln_hd1_17_03 = "Tem um helicóptero esperando para pegar vocês. Mas quando vocês o chamarem, as coisas vão ficar violentas. Então escolham o melhor momento.",
	pln_hd1_18_01 = "Estão vendo essa jenala aberta? Desde que as coisas estejam calmas, vocês podem assegurar os artefatos por ela. Um cara vai pegar eles para gente.",
	pln_hd1_19_02 = "Tem uma zip-line que pode ajudar vocês a assegurar o nosso roubo, perfeito e quieto. Certifiquem-se de usá-la.",
	pln_hd1_20_01 = "Porra! A zip-line é inútil agora. Podem esquecer de assegurar o roubo por ela!",
	pln_hd1_20_02 = "O cara da zip-line teve que cair fora, então esqueçam de assegurar coisas pela janela. ",
	pln_hd1_21_01 = "O operador das câmeras já era. Vocês não precisam mais se preocupar com elas!",
	pln_hd1_21_02 = "Esse segurança já era. Vocês não precisam mais se estressar com as câmeras.",
	pln_hd1_21_03 = "O segurança está cego. As câmeras estão fora de ação. Isso deve facilitar as coisas.",
	pln_hd1_22_02 = "O alarme dos vidros já era. Se quiserem fazer uma graninha extra, essa é a hora. Sempre é possível vender esses artefatos.",
	pln_hd1_22_03 = "O alarme dos vidros está desativado. Encarnem o Dr.Jones pessoal, roubem algumas antiguidades. Eu conheço um cara que pode negociar essas coisas. Confiem.",
	pln_hd1_23_01 = "Ouçam, cada caminho que vocês criarem só vai durar por um tempo. Vocês podem criar outro - só certifiquem-se que não estejam em cima dos pisos.",
	pln_hd1_23_02 = "O caminho vai ser resetado depois de um tempo determinado. Vocês sempre podem criar um novo. Só certifiquem-se que não estejam em cima dele quando o tempo acabar,",
	pln_hd1_25_01 = "Dêem uma olhada nesse lugar. Não parece um cofre comum, huh?",
	pln_hd1_26_01 = "Consigam um cartão e enfiem ele no temporizador. Chequem os cientistas para consegui-lo.",
	pln_hd1_26_03 = "Os temporizadores podem ser derrubados com cartões. Os civis os têm. Estão vendo aonde isto está nos levando?",
	pln_hd1_29_01 = "Nós viemos, nós vimos... Nós roubamos um monte de merda antiga de Roma.",
	pln_hd1_29_03 = "Não se distraiam, pessoal. Fiquem de olho no... Oooh esperem, isso é do Primeiro Triunvirato?!",
	pln_hd1_30_03 = "Supostamente, foi Genghis que encontrou esse Diamante. Digamos que ele aproveitou bastante.",
	pln_hd1_31_01 = "Woah, olhem essas coisas. Não podem ser legítimas... Podem?",
	pln_hd1_31_03 = "Eu amava essa sala quando criança, mas hoje... Me dá náuseas.",
	pln_hd1_33_02 = "Tem umas paradas boas nesses vidros. Mas algumas merdas falsificadas também. Procurem pelos adesivos de alarmes. Eles vão dar uma ideia do que é bom.",
	pln_hd1_33_03 = "Umas paradas boas nesses vidros. Algumas merdas também. Se vocês forem roubar, roubem as coisas de valor - procurem pelos vidros com adesivos de alarme. Tenho certeza que uma gangue inteligente como vocês pode descobrir.",
	pln_hd1_34_02 = "Parece que a polícia inteira está indo até vocês! Vocês não vão escapar dirigindo. Acendam um sinalizador, e nós vamos arranjar uma saída pelos ares.",
	pln_hd1_36_02 = "Está cheio. Aguentem firme aí. O helicóptero vai voltar.",
	pln_hd1_37_03 = "Porra, esse era o piso errado! ",
	pln_hd1_39_01 = "Silêncio... Silêncio. Vocês estão indo bem.",
	pln_hd1_39_02 = "Mantenham tudo quieto, pessoal.",
	pln_hd1_40_01 = "Os policiais foderam o temporizador! Coloquem-o para funcionar de novo!",
	pln_hd1_41_02 = "Tudo isso aqui não vai servir para nada se vocês não colocarem o temporizador para rodar de novo.",
	pln_hd1_42_01 = "Ótimo. Agora mantenham os policiais longe.",
	pln_hd1_42_02 = "Perfeito. Agora, por favor, mantenham os policiais longe do temporizador.",
	pln_hd1_44_01 = "Certo. Agora me conectem ao terminal de novo.",
	pln_hd1_44_02 = "Me conectem de novo.",
	
	-- Objetivos
	hud_ind_new_11_hl = "Fuga disponível!",
	hud_ind_new_8_hl = "Chegue no Diamante",
	hud_ind_new_1 = "Encontre um jeito de entrar no museu",
	hud_ind_new_6 = "Encontre um jeito de entrar na sala do Diamante.",
	hud_ind_new_1_hl = "Entre no museu",
	hud_ind_new_4 = "Ache uma forma de entrar no museu.",
	hud_ind_new_9_hl = "Roube o Diamante",
	hud_ind_new_5_fix_hl = "Reconecte o dispositivo",
	hud_ind_new_loud_2_hl = "Hackeie o lockdown de segurança",
	hud_ind_new_8 = "Vão até o Diamante mas fiquem espertos para os pisos com alarmes. Use o caminho da caixa de circuito.",
	hud_ind_new_4_hl = "Façam o caminho de vocês",
	hud_ind_new_5_wait_hl = "Espere pelo temporizador",
	hud_ind_new_loud_1_hl = "Encontre a sala de segurança",
	hud_ind_new_5_wait = "Espere pelo temporizador terminar.",
	hud_ind_new_esc_2 = "Espere o helicóptero chegar no museu.",
	hud_ind_new_brute_2_hl = "Fure o alçapão",
	hud_ind_new_7 = "Os pisos da sala do Diamante são sensíveis ao toque, criem um caminho seguro através deles.",
	hud_ind_new_10 = "Encontre um jeito de sair e assegure o Diamante.",
	hud_ind_new_10_hl = "Assegure o Diamante",
	hud_ind_new_11 = "Vão até o veículo de fuga e sumam daí!",
	hud_ind_new_2 = "Encontre em qual parte do museu o Diamante está.",
	hud_ind_new_2_hl = "Encontre onde o Diamante está",
	hud_ind_new_3 = "Hackeiem a caixa de circuito para desativar as primeiras proteções do Diamante.",
	hud_ind_new_3_hl = "Hackeiem as caixas de circuito",
	hud_ind_new_5 = "Usem os cartões dos funcionários para passar pelos temporizadores. Se as coisas ficarem violentas, hackeiem os painéis.",
	hud_ind_new_5_fix = "Certifiquem-se de reconectar o dispositivo",
	hud_ind_new_5_hl = "Passem pelos temporizadores",
	hud_ind_new_6_hl = "Entre na sala do Diamante",
	hud_ind_new_7_hl = "Encontrem um caminho através do chão, é uma armadilha.",
	hud_ind_new_9 = "É hora de brilhar - roubem o Diamante.",
	hud_ind_new_brute_1 = "Se mantenham longe do gás e esperem até ele se dissipar.",
	hud_ind_new_brute_1_hl = "Espere o gás se dissipar",
	hud_ind_new_brute_2 = "Furem o alçapão.",
	hud_ind_new_esc_1 = "Chame um helicóptero vir e tirar vocês do museu.",
	hud_ind_new_esc_1_hl = "Chame o helicóptero",
	hud_ind_new_esc_2_hl = "Espere pelo helicóptero chegar",
	hud_ind_new_loud_1 = "Encontre a sala de segurança em algum lugar no museu.",
	hud_ind_new_loud_2 = "Hackeie o lockdown de segurança.",
		})
end)