	loc_mex_cook_07_06 = "Esperem a porra de um segundo!",--Wait a blerrie minute!
	loc_mex_cook_07_07 = "Desculpe amigos, não estava certo.",--Sorry friends, that wasn't right.
	loc_mex_cook_07_08 = "Espera!",--Wait!
	loc_mex_cook_07_09 = "Para! Não colocaram ainda, né?",--Stop! You didn't put it in yet, did you?
