		loc_des_intro_01b = "Fortuna e glória, garoto.",
		loc_des_intro_04b = "Se eles tiverem uma daquelas armas de raio, eu vou querer uma.",
		loc_des_intro_03a = "Que tipo de merda a gente vai achar?",
		loc_des_intro_03d = "Ei, acabei de pensar nisso. A gente deveria ter trazido roupas de proteção?",
		loc_des_intro_05e = "Me pergunto se eles manteram o avião da Amelia Earhart por aqui.",
		loc_des_intro_05c = "Sempre me perguntei como os policiais deles se parecem.",
		loc_des_intro_02d = "Sério mesmo, se eles tiverem as armas de raio, eu vou pegar mesmo.",
		loc_des_intro_05d = "Todas as coisas consideradas, eu prefiro voltar pra Vegas.",
		loc_des_intro_01a = "Podem me lembrar a razão pela qual a gente tá fazendo isso?",
		loc_des_intro_05b = "Eles tem homens ultramente treinados.",
		loc_des_intro_03c = "Um pouquinho de drama aí, Lockezinho.",
		loc_des_intro_04e = "Se alguém descubrir o que realmente aconteceu com o Elvis, me contem!",
